/* global document, window */
import $ from 'jquery';
import _ from 'underscore';
import Modules from './utils/modules';
import log from './lib/log';
import Detect from './utils/detect';

/**
 * Controls the client side changes for the application and game
 * @class
 */
export default class App {
  /**
   * Load all the different windows/UI portions on the index page
   * relevant for use in the game, set the zoom factor, update the
   * orientation and then load the login screen
   */
  constructor() {
    log.debug('App - constructor()');

    /**
     * Configuration for the client application
     * loaded from ./assets/data/config.json
     * @type {Object}
     */
    this.config = null;

    /**
     * An instance of the DOM $('body') element
     * @type {Object}
     */
    this.body = $('body');

    /**
     * An instance of the DOM $('#content') element
     * @type {Object}
     */
    this.wrapper = $('#content');

    /**
     * An instance of the DOM $('#container') element
     * @type {Object}
     */
    this.container = $('#container');

    /**
     * An instance of the DOM $(window) element
     * @type {Object}
     */
    this.window = $(window);

    /**
     * An instance of the DOM $('#canvasLayers') element
     * @type {Object}
     */
    this.canvas = $('#canvasLayers');

    /**
     * An instance of the DOM $('#border') element
     * @type {Object}
     */
    this.border = $('#border');

    /**
     * An instance of the DOM $('#modal') element
     * @type {Object}
     */
    this.intro = $('#modal');

    /**
     * An instance of the DOM $('#loginButton') element
     * @type {Object}
     */
    this.loginButton = $('#loginButton');

    /**
     * An instance of the DOM $('#play') element
     * @type {Object}
     */
    this.createButton = $('#play');

    /**
     * An instance of the DOM $('#newCharacter') element
     * @type {Object}
     */
    this.registerButton = $('#newCharacter');

    /**
     * An instance of the DOM $('#helpButton') element
     * @type {Object}
     */
    this.helpButton = $('#helpButton');

    /**
     * An instance of the DOM $('#cancelButton') element
     * @type {Object}
     */
    this.cancelButton = $('#cancelButton');

    /**
     * An instance of the DOM $('#yes') element
     * @type {Object}
     */
    this.yes = $('#yes');

    /**
     * An instance of the DOM $('#no') element
     * @type {Object}
     */
    this.no = $('#no');

    /**
     * An instance of the DOM $('.loader') element
     * @type {Object}
     */
    this.loading = $('.loader');

    /**
     * An instance of the DOM $('.loader.message') element
     * @type {Object}
     */
    this.loadingMsg = $('.loader.message');

    /**
     * An instance of the DOM $('.error.message') element
     * @type {Object}
     */
    this.errorMsg = $('.error.message');

    /**
     * An instance of the DOM $('#respawn') element
     * @type {Object}
     */
    this.respawn = $('#respawn');

    /**
     * An instance of the DOM $('#rememberMe') element
     * @type {Object}
     */
    this.rememberMe = $('#rememberMe');

    /**
     * An instance of the DOM $('#guest') element
     * @type {Object}
     */
    this.guest = $('#guest');

    /**
     * An instance of the DOM $('#toggle-about') element
     * @type {Object}
     */
    this.about = $('#toggle-about');

    /**
     * An instance of the DOM $('#toggle-credits') element
     * @type {Object}
     */
    this.credits = $('#toggle-credits');

    /**
     * An instance of the DOM $('#toggle-git') element
     * @type {Object}
     */
    this.git = $('#toggle-git');

    /**
     * An instance of the DOM $('#loginNameInput') and $('#loginPasswordInput') element
     * @type {Object[]}
     */
    this.loginFields = [$('#loginNameInput'), $('#loginPasswordInput')];

    /**
     * Register fields
     * @type {Array}
     */
    this.registerFields = [];

    /**
     * An instance of Game class
     * @type {Game}
     */
    this.game = null;

    /**
     * Used to determine if the player is logging in as a guest or not
     * @type {Boolean}
     */
    this.guestLogin = false;

    /**
     * Zoom percentage from 0 to 1
     * @type {Number}
     */
    this.zoomFactor = 1;

    /**
     * Used to figure out whether or not to show the login screen
     * @type {Boolean}
     */
    this.loggingIn = false;

    /**
     * The width of the player's browser window
     * @type {Number}
     */
    this.width = window.innerWidth;

    /**
     * The height of the player's browser window
     * @type {Number}
     */
    this.height = window.innerHeight;

    /**
     * The orientation of the player's device
     * @type {String}
     */
    this.orientation = 'landscape';

    /**
     * Callback function when this class is done loading
     * @type {Function}
     */
    this.readyCallback = null;

    /**
     * Used to display loading status messages
     * @type {String}
     */
    this.statusMessage = null;

    // auto-binding
    this.keydownEventListener = this.keydownEventListener.bind(this);
    this.keyupEventListener = this.keyupEventListener.bind(this);
    this.mousemoveEventListener = this.mousemoveEventListener.bind(this);
    this.mousemoveEventListener = this.mousemoveEventListener.bind(this);
    this.canvasClickEventListener = this.canvasClickEventListener.bind(this);

    this.sendStatus('You should turn back now...');

    this.zoom();
    this.updateOrientation();
    this.load();
  }

  /**
   * Add click listeners for the different parts of the UI
   */
  load() {
    log.debug('App - load()');

    this.loginButton.click(() => this.login());
    this.createButton.click(() => this.login());
    this.loadCharacter();
    this.yes.click(() => this.welcomeContinue());
    this.no.click(() => this.welcomeContinue());
    this.rememberMe.click(() => this.rememberLogin());
    this.guest.click(() => this.loginAsGuest());

    this.registerButton.click(() => this.displayScreen('loadCharacter', 'createCharacter'));
    this.cancelButton.click(() => this.displayScreen('createCharacter', 'loadCharacter'));
    this.about.click(() => this.displayScroll('about'));
    this.credits.click(() => this.displayScroll('credits'));
    this.git.click(() => this.displayScroll('git'));
    this.respawn.click(() => this.respawnPlayer());

    window.scrollTo(0, 1); // why do we do this?
    this.window.resize(this.zoom());

    $.getJSON('./src/client/config.json', (json) => {
      log.debug('App - loading client config');
      this.config = json;

      if (this.readyCallback) {
        log.debug('App - loading client config done - readyCallback()');
        this.readyCallback();
      }
    });

    $(document).on('keydown', this.keydownEventListener);
    $(document).on('keyup', this.keyupEventListener);
    $(document).on('mousemove', this.mousemoveEventListener);
    this.canvas.on('click', this.canvasClickEventListener);

    // this.canvas.click(this.canvasClickEventListener);

    $('input[type="range"]').on('input', () => {
      this.updateRange(this);
    });
  }

  /**
   * Dismissing the welcome screen when the application has
   * finished loading
   */
  welcomeContinue() {
    log.debug('App - welcomeContinue()');

    if (!this.game) {
      return false;
    }

    // makes sure the welcome screen doesn't appear again
    this.game.storage.data.welcome = false;
    this.game.storage.save();
    this.body.removeClass('welcomeMessage');
    return true;
  }

  /**
   * Try to connect and login to the game
   * @private
   * @return {Boolean}
   */
  login() {
    log.debug('App - login()');

    if (
      this.loggingIn
      || !this.game
      || !this.game.loaded
      || this.statusMessage
      || !this.verifyForm()
    ) return false;

    // this.toggleLogin(true);
    this.game.connect();
    return true;
  }

  /**
   * Try to login as a guest
   * @private
   * @return {Boolean}
   */
  loginAsGuest() {
    log.debug('App - loginAsGuest()');

    if (!this.game) {
      return false;
    }

    this.guestLogin = true;
    this.login();
    return true;
  }

  /**
   * Switch to the load character screen
   * @private
   * @return {Boolean}
   */
  loadCharacter() {
    log.debug('App - loadCharacter()');

    if (
      this.wrapper.hasClass('about')
      || this.wrapper.hasClass('credits')
      || this.wrapper.hasClass('git')
    ) {
      this.wrapper.removeClass('about credits git');
      this.displayScroll('loadCharacter');
      return true;
    }

    return false;
  }

  /**
   * Tells the game to toggle remembering this player,
   * if true will store player information in local storage
   *
   * @TODO update this to use the database instead
   * @private
   * @return {Boolean}
   */
  rememberLogin() {
    log.debug('App - rememberLogin()');

    if (!this.game || !this.game.storage) {
      return false;
    }

    const active = this.rememberMe.hasClass('active');

    this.rememberMe.toggleClass('active');
    this.game.storage.toggleRemember(!active);
    return true;
  }

  /**
   * Tells the game to respawn the player
   * @private
   * @return {Boolean}
   */
  respawnPlayer() {
    log.debug('App - respawnPlayer()');

    if (!this.game || !this.game.player || !this.game.player.dead) {
      return false;
    }

    this.game.respawn();
    return true;
  }

  /**
   * Handles key down presses
   * @param {Object} event keypress event
   * @return {Boolean}
   */
  keydownEventListener(event) {
    log.debug('App - keydownEventListener()', event);

    const key = event.which;

    if (!this.game) {
      return false;
    }

    this.body.focus();

    if (key === Modules.Keys.Enter && !this.game.started) {
      this.login();
      return false;
    }

    if (this.game.started) {
      this.game.onInput(Modules.InputType.Key, key);
    }

    return true;
  }

  /**
   * Handles key up presses
   * @param {Object} event keypress event
   * @return {Boolean}
   */
  keyupEventListener(event) {
    log.debug('App - keyupEventListener()', event);

    const key = event.which;

    if (!this.game || !this.game.started) {
      return false;
    }

    this.game.input.keyUp(key);
    return true;
  }

  /**
   * Handles mouse movements
   * @param {Object} event mouse event
   */
  mousemoveEventListener(event) {
    // log.debug('App - mousemoveEventListener()', event, this);

    if (!this.game || !this.game.input || !this.game.started) {
      return false;
    }

    this.game.input.setCoords(event);
    this.game.input.moveCursor();

    return true;
  }

  /**
   * Handles clicks on the HTML5 canvas
   * @param {Object} event click event
   * @return {Boolean}
   */
  canvasClickEventListener(event) {
    log.debug('App - canvasClickEventListener()');

    if (!this.game || !this.game.started) {
      return false;
    }

    window.scrollTo(0, 1);
    this.game.input.handle(Modules.InputType.LeftClick, event);

    return true;
  }

  /**
   * Handles zooming the container to scale the
   * game to fit porportionally with the player's window size
   */
  zoom() {
    log.debug('App - zoom()');

    const containerWidth = this.container.width();
    const containerHeight = this.container.height();
    const windowWidth = this.window.width();
    const windowHeight = this.window.height();
    let zoomFactor = windowWidth / containerWidth;

    if (containerHeight + 50 >= windowHeight) {
      zoomFactor = windowHeight / (containerHeight + 50);
    }

    if (this.getScaleFactor() === 3) {
      zoomFactor -= 0.1;
    }

    // this.body.css({
    //   zoom: zoomFactor,
    //   '-moz-transform': `scale(${zoomFactor})`,
    // });

    this.border.css('top', 0);
    // this.zoomFactor = zoomFactor;
  }

  /**
   * Fades the menu
   */
  fadeMenu() {
    log.debug('App - fadeMenu()');

    this.updateLoader(null);

    setTimeout(() => {
      this.body.addClass('game');
      this.body.addClass('started');
      this.body.removeClass('intro');
    }, 500);
  }

  /**
   * Shows the menu
   */
  showMenu() {
    log.debug('App - showMenu()');

    this.body.removeClass('game');
    this.body.removeClass('started');
    this.body.addClass('intro');
  }

  /**
   * Changes from one screen to another
   * @param {String} origin the old screen to hide
   * @param {String} destination the new screen to show
   */
  displayScreen(origin, destination) {
    log.debug('App - displayScreen()', origin, destination);

    if (!destination || this.loggingIn) {
      return;
    }

    this.cleanErrors();
    // this.wrapper.removeClass(origin).addClass(destination);
    $(`#${origin}`).css('display', 'none');
    $(`#${destination}`).css('display', 'block');
  }

  /**
   * Display the given screen
   * @param {String} content - the screen to display
   */
  displayScroll(content) {
    log.debug('App - displayScroll()', content);

    const state = this.wrapper.attr('class');

    if (this.game.started) {
      this.wrapper.removeClass().addClass(content);
      this.body.removeClass('credits legal about').toggleClass(content);

      if (this.game.player) {
        this.body.toggleClass('death');
      }

      if (content !== 'about') {
        this.helpButton.removeClass('active');
      }
    } else if (state !== 'animate') {
      this.displayScreen(state, state === content ? 'loadCharacter' : content);
    }
  }

  /**
   * Verify the active form
   */
  verifyForm() {
    log.debug('App - verifyForm()');

    const activeForm = this.getActiveForm();

    if (activeForm === 'null') {
      return true;
    }

    switch (activeForm) {
      case 'loadCharacter': return this.verifyLoginForm();
      case 'createCharacter': return this.verifyJoinForm();
      default: break;
    }

    return true;
  }

  /**
   * Verify the input fields on the join form
   * @return {Boolean} true if valid
   */
  verifyJoinForm() {
    log.debug('App - verifyJoinForm()');

    const characterName = $('#registerNameInput');
    const registerPassword = $('#registerPasswordInput');
    const email = $('#registerEmailInput');
    const registerPasswordConfirmation = $('#registerPasswordConfirmationInput');

    if (this.registerFields.length === 0) {
      this.registerFields = [
        characterName,
        registerPassword,
        registerPasswordConfirmation,
        email,
      ];
    }

    if (!characterName.val()) {
      this.sendError(characterName, 'A username is necessary you silly.');
      return false;
    }

    if (!registerPassword.val()) {
      this.sendError(registerPassword, 'You must enter a password.');
      return false;
    }

    if (registerPasswordConfirmation.val() !== registerPassword.val()) {
      this.sendError(
        registerPasswordConfirmation,
        'The passwords do not match!',
      );
      return false;
    }

    if (!email.val() || !this.verifyEmail(email.val())) {
      this.sendError(email, 'An email is required!');
      return false;
    }

    return true;
  }

  /**
   * Check to see if the player has entered a valid username and password
   * before attempting to login
   * @return {Boolean} true if valid
   */
  verifyLoginForm() {
    log.debug('App - verifyLoginForm()');

    const nameInput = $('#loginNameInput');
    const passwordInput = $('#loginPasswordInput');

    if (this.loginFields.length === 0) {
      this.loginFields = [nameInput, passwordInput];
    }

    if (!nameInput.val() && !this.isGuest()) {
      this.sendError(nameInput, 'Please enter a username.');
      return false;
    }

    if (!passwordInput.val() && !this.isGuest()) {
      this.sendError(passwordInput, 'Please enter a password.');
      return false;
    }

    return true;
  }

  /* eslint-disable */
  /**
   * Verify this is a valid email address
   * @param  {String} email player's email address
   * @return {Boolean} true if valid
   */
  verifyEmail(email) {
    log.debug('App - verifyEmail()', email);

    return /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
      email,
    );
  }
  /* eslint-enable */

  /**
   * Display a status message to the client
   * @param {String} message the message
   */
  sendStatus(message) {
    log.debug('App - sendStatus()', message);

    this.cleanErrors();

    this.statusMessage = message;

    if (!message) {
      this.loadingMsg.html('');
      this.loading.hide();
      return;
    }

    this.loading.show();
    this.loadingMsg.html(message);
  }

  /**
   * Set this input field as having an error
   * @param {Object} field the Jquery instance of the field that has the error
   * @param {String} error the error message to display telling the player
   * that this field has an issue
   */
  sendError(field, error) {
    log.debug('App - sendError()', field, error);

    this.cleanErrors();
    this.errorMsg.html(error);
    this.errorMsg.show();

    if (!field) {
      return;
    }

    field.addClass('field-error').select();
    field.bind('keypress', (event) => {
      field.removeClass('field-error');
      $('.validation-error').remove();
      $(this).unbind(event);
    });
  }

  /**
   * Clean up any current form validation errors
   * on the active form's input fields
   */
  cleanErrors() {
    log.debug('App - cleanErrors()');

    const activeForm = this.getActiveForm();
    const fields = activeForm === 'loadCharacter'
      ? this.loginFields
      : this.registerFields;

    for (let i = 0; i < fields.length; i += 1) {
      fields[i].removeClass('field-error');
    }

    $('.validation-error').remove();
    $('.status').remove();
  }

  /**
   * Return the active form from the top wrapper's class name
   * @return {String} active form's ID
   */
  getActiveForm() {
    log.debug('App - getActiveForm()');

    return this.wrapper && this.wrapper[0] && this.wrapper[0].className;
  }

  /**
   * Returns true if the join form is displayed
   * @return {Boolean} true if on the join form
   */
  isRegistering() {
    log.debug('App - isRegistering()');

    return this.getActiveForm() === 'createCharacter';
  }

  /**
   * Returns true if this player is playing as a guest
   * @return {Boolean} true if player is a guest
   */
  isGuest() {
    log.debug('App - isGuest()');

    return this.guestLogin;
  }

  /**
   * Call the game's resizer function
   */
  resize() {
    log.debug('App - resize()');

    if (this.game) {
      this.game.resize();
    }
  }

  /**
   * Set the app to use a new instance of the game
   */
  setGame(game) {
    log.debug('App - setGame()', game);

    this.game = game;
    window.wtf = this;
  }

  /**
   * Checks to see if the window has a web worker
   * @return {Boolean} returns true if the window has an active web worker
   */
  hasWorker() {
    log.debug('App - hasWorker()');

    return !!this.window.Worker;
  }

  /**
   * Figures out the current scale factor to use
   * for assets
   * @return {Number} 1 = mobile, 2 = desktop and 3 = tablet
   */
  getScaleFactor() {
    const mobile = 1;
    const tablet = 2;
    const desktop = 3;
    const tabletOrDesktop = this.width <= 1500 || this.height <= 870
      ? tablet
      : desktop;

    /**
     * These are raw scales, we can adjust
     * for up-scaled rendering in the actual
     * rendering file.
     */
    return this.width <= 1000
      ? mobile
      : tabletOrDesktop;
  }

  /**
   * Revert the loader message to it's default state
   * of 'Connecting to server...'
   */
  revertLoader() {
    log.debug('App - revertLoader()');

    this.updateLoader('Connecting to server...');
  }

  /**
   * Update the loader to show a specific message
   * @param  {String} message the loader message to display
   */
  updateLoader(message) {
    log.debug('App - updateLoader()', message);

    if (!message) {
      this.loading.hide();
      this.loadingMsg.html('');
      return;
    }

    this.loading.show();
    this.loadingMsg.html(message);
  }

  /**
   * Toggle displaying the login form
   * @param  {Boolean} toggle true to hide, false to show
   */
  toggleLogin(toggle) {
    log.debug('App - toggleLogin()', toggle);

    this.revertLoader();
    this.toggleTyping(toggle);
    this.loggingIn = toggle;

    if (toggle) {
      this.loading.hide();
      this.loginButton.addClass('disabled');
      this.registerButton.addClass('disabled');
    } else {
      this.loading.hide();
      this.loginButton.removeClass('disabled');
      this.registerButton.removeClass('disabled');
    }
  }

  /**
   * Toggle the read only attribute on the login fields
   * and form registration fields
   * @param  {String} state the state to update the fields to
   */
  toggleTyping(state) {
    log.debug('App - toggleTyping()', state);

    if (this.loginFields) {
      _.each(this.loginFields, (field) => {
        field.prop('readonly', state);
      });
    }

    if (this.registerFields) {
      _.each(this.registerFields, (field) => {
        field.prop('readOnly', state);
      });
    }
  }

  /**
   * Update the range of and object
   * using a linear gradient on the background color
   */
  updateRange(field) {
    log.debug('App - updateRange()', field);

    const obj = $(field);
    const val = (obj.val() - obj.attr('min')) / (obj.attr('max') - obj.attr('min'));

    obj.css(
      'background-image',
      `-webkit-gradient(linear, left top, right top, color-stop(${val}, #4d4d4d), color-stop(${val}, #C5C5C5)`,
    );
  }

  /**
   * Updates the orientation of the application
   * to match the orientation of the device the
   * player is using
   */
  updateOrientation() {
    log.debug('App - updateOrientation()');

    // this.zoom();
    this.orientation = this.getOrientation();
  }

  /**
   * Returns the orientation of the device the user is on
   * @return {String} portrait|landscape
   */
  getOrientation() {
    log.debug('App - getOrientation()');

    return this.height > this.width ? 'portrait' : 'landscape';
  }

  /**
   * Returns the zoom factor
   * @return {Number} a value from 0 to 1
   */
  getZoom() {
    // log.debug('App - getZoom()');

    return this.zoomFactor;
  }

  /**
   * The callback function when this app class is ready
   * and done loading
   * @param {Function} callback the callback function when
   * this application class is done loading
   */
  onReady(callback) {
    log.debug('App - onReady()', callback);

    this.readyCallback = callback;
  }

  /**
   * Check if the player is using a phone
   * @return {Boolean} true if a phone
   */
  isMobile() {
    log.debug('App - isMobile()');

    return this.getScaleFactor() < 2;
  }

  /**
   * Check if the player is using a tablet
   * @return {Boolean} true if a tablet
   */
  isTablet() {
    log.debug('App - isTablet()');

    return Detect.isIpad() || (Detect.isAndroid() && this.getScaleFactor() > 1);
  }
}

/* global document */
import _ from 'underscore';
import log from '../lib/log';
import Modules from '../utils/modules';

/**
 * Contorls audio and sfx in the game
 * @class
 */
export default class Audio {
  /**
   * Default constructor
   * @param {Game} instance of the game
   */
  constructor(game) {
    /**
    * An instance of the game
    * @type {Game}
    */
    this.game = game;

    /**
    * An object with all of the music and sfxs
    * @type {Object}
    */
    this.audibles = {};

    /**
    * The format of the audibles
    * @type {String}
    */
    this.format = 'mp3';

    /**
    * An instance of the song
    * @type {Object}
    */
    this.song = null;

    /**
    * The name of the song
    * @type {String}
    */
    this.songName = null;

    /**
    * Whether or not music is on in the game
    * @type {Boolean}
    */
    this.enabled = true;


    /**
    * All the music in the game
    * @type {Object}
    */
    this.music = {};

    /**
    * All the SFX in the game
    * @type {Object}
    */
    this.sfx = {};

    this.load();
  }

  /**
   * Loads the music and sfx in the game
   */
  load() {
    this.music = {
      codingroom: false,
      smalltown: false,
      village: false,
      beach: false,
      spookyship: false,
      meadowofthepast: false,
    };

    this.sfx = {
      loot: false,
      hit1: false,
      hit2: false,
      hurt: false,
      heal: false,
      chat: false,
      revive: false,
      death: false,
      firefox: false,
      achievement: false,
      kill1: false,
      kill2: false,
      noloot: false,
      teleport: false,
      chest: false,
      npc: false,
      'npc-end': false,
    };
  }

  /**
   * Default constructor
   * @param {String} path to the audio file
   * @param {String} name the name of the audio file
   * @param {Array} channels an array of audio channels
   * @param {Function} callback the function to call when done parsing
   * the audio file
   */
  parse(path, name, channels, callback) {
    const fullPath = `${path + name}.${this.format}`;
    const sound = document.createElement('audio');

    sound.addEventListener(
      'canplaythrough',
      function callbackPlayThrough() {
        this.removeEventListener('canplaythrough', callbackPlayThrough, false);

        if (callback) {
          callback();
        }
      },
      false,
    );

    sound.addEventListener(
      'error',
      () => {
        log.error(
          `The audible: ${name} could not be loaded - unsupported extension?`,
        );

        this.audibles[name] = null;
      },
      false,
    );

    sound.preload = 'auto';
    sound.autobuffer = true;
    sound.src = fullPath;
    sound.load();

    this.audibles[name] = [sound];

    _.times(channels - 1, () => {
      this.audibles[name].push(sound.cloneNode(true));
    });

    if (name in this.music) {
      this.music[name] = true;
    } else if (name in this.sfx) {
      this.sfx[name] = true;
    }
  }

  /**
   * Play a specific audio file
   * @param {String} type Modules.AudioTypes.Music|Modules.AudioTypes.SFX
   * @param {String} name the name of the audio file
   * @return {Boolean}
   */
  play(type, name) {
    if (!this.isEnabled() || !this.fileExists(name)) {
      log.debug('Audio - play() - audio is disabled');
      return false;
    }

    this.song = this.get(name);

    switch (type) {
      case Modules.AudioTypes.Music:
        this.fadeOut(() => {
          this.reset(this.song);
        });

        if (!this.song) {
          log.debug(`Audio - play() - ${name} could not be loaded`);
          return false;
        }

        this.song.volume = 0;
        this.song.play();
        this.fadeIn();
        break;
      case Modules.AudioTypes.SFX:
        if (!this.sfx[name]) {
          this.parse('assets/audio/sfx/', name, 4);
        }

        if (!this.song) {
          log.debug(`Audio - play() - ${name} sfx could not be loaded`);
          return false;
        }

        this.song.volume = this.getSFXVolume();
        this.song.play();
        break;
      default:
        return false;
    }

    return true;
  }

  /**
   * Update a song to replay, fade in, fade out or reset
   * @return {Boolean}
   */
  update() {
    log.debug('Audio - update()', this.songName);

    if (!this.isEnabled()) {
      log.debug('Audio - update() - audio is disabled');
      return false;
    }

    const song = this.getMusic(this.songName);

    if (song && !(this.song && this.song.name === song.name)) {
      if (this.game.renderer.mobile) {
        this.reset(this.song);
      } else if (this.song) {
        this.fadeSongOut();
      }

      if (song.name in this.music && !this.music[song.name]) {
        this.parse('audio/music/', song.name, 1);

        const music = this.audibles[song.name][0];

        music.loop = true;
        music.addEventListener(
          'ended',
          () => {
            music.play();
          },
          false,
        );
      }

      this.play(Modules.AudioTypes.Music, song.name);
    } else if (this.game.renderer.mobile) {
      this.reset(this.song);
    } else {
      this.fadeSongOut();
    }

    return true;
  }

  /**
   * Fade in the active song
   * @return {Boolean}
   */
  fadeIn() {
    if (!this.song || this.song.fadingIn) {
      return false;
    }

    this.clearFadeOut(this.song);

    this.song.fadingIn = setInterval(() => {
      if (this.song && this.song.volume + 0.02 < 1) {
        this.song.volume += 0.02;
      } else if (this.song) {
        this.song.volume = 1;
      }

      if (this.song && this.song.volume >= this.getMusicVolume() - 0.02) {
        this.song.volume = this.getMusicVolume();
        this.clearFadeIn(this.song);
      }
    }, 100);

    return true;
  }

  /**
   * Fade out the active song
   * @param {Function} callback trigger this callback when the fade out is done
   * @return {Boolean}
   */
  fadeOut(callback) {
    if (!this.song || this.song.fadingOut) {
      return false;
    }

    this.clearFadeIn(this.song);

    this.song.fadingOut = setInterval(() => {
      if (this.song && this.song.volume - 0.08 > 0) {
        this.song.volume -= 0.08;
      } else if (this.song) {
        this.song.volume = 0;
      }

      if (this.song && this.song.volume <= 0.08) {
        this.song.volume = 0;

        if (callback) {
          callback(this.song);
        }

        clearInterval(this.song.fadingOut);
      }
    }, 100);

    return true;
  }

  /**
   * Triggers the song to fade out and then resets it
   * @return {Boolean}
   */
  fadeSongOut() {
    if (!this.song) {
      return false;
    }

    this.fadeOut(() => {
      this.reset(this.song);
    });

    this.song = null;
    return true;
  }

  /**
   * Clear the fade in interval
   * @return {Boolean}
   */
  clearFadeIn() {
    if (this.song.fadingIn) {
      clearInterval(this.song.fadingIn);
      this.song.fadingIn = null; // eslint-disable-line
      return true;
    }
    return false;
  }

  /**
   * Clears the fade out interval
   * @return {Boolean}
   */
  clearFadeOut() {
    if (this.song.fadingOut) {
      clearInterval(this.song.fadingOut);
      this.song.fadingOut = null; // eslint-disable-line
      return true;
    }
    return false;
  }

  /**
   * Resets a song so that it's ready to play from the beginning again
   * @return {Boolean}
   */
  reset(song) {
    if (song) {
      this.song = song;
    }

    if (!this.song || !this.song.readyState > 0) {
      return false;
    }

    this.song.pause();
    this.song.currentTime = 0;
    return true;
  }

  /**
   * Stop playing a song
   * @return {Boolean}
   */
  stop() {
    if (!this.song) {
      return false;
    }

    this.fadeOut(() => {
      this.reset(this.song);
      this.song = null;
    });

    return true;
  }

  /**
   * checks to see if this song file exists in the music list
   * @param {String} name the name of the song
   * @return {Boolean} returns true if it exists
   */
  fileExists(name) {
    return name in this.music || name in this.sfx;
  }

  /**
   * Return the song
   * @param {String} name the name of the song
   * @return {Boolean}
   */
  get(name) {
    if (!this.audibles[name]) {
      return null;
    }

    let audible = _.detect(this.audibles[name], sound => sound.ended || sound.paused);

    if (audible && audible.ended) {
      audible.currentTime = 0;
    } else {
      audible = this.audibles[name][0]; // eslint-disable-line
    }

    return audible;
  }

  /**
   * Returns an object with the sound and the name of the sound
   * @param {String} name the name of the music
   * @return {{ sound: HTMLAudioElement, name: String }}
   */
  getMusic(name) {
    return {
      sound: this.get(name),
      name,
    };
  }

  /**
   * Set the volume level on the sound
   * @param {Number} volume the volume level
   * @return {Boolean}
   */
  setSongVolume(volume) {
    this.song.volume = volume;
    return true;
  }

  /**
   * Return the SFX volume
   * @return {Number}
   */
  getSFXVolume() {
    return this.game.storage.data.settings.sfx / 100;
  }

  /**
   * Return the music volume
   * @return {Number}
   */
  getMusicVolume() {
    return this.game.storage.data.settings.music / 100;
  }

  /**
   * Returns true if the users sound is enabled
   * @return {Boolean}
   */
  isEnabled() {
    return this.game.storage.data.settings.soundEnabled && this.enabled;
  }
}

import _ from 'underscore';
import $ from 'jquery';
import Blob from '../renderer/bubbles/blob';

/**
 * Creates player speach bubbles
 * @class
 */
export default class Bubble {
  /**
   * Default constructor
   * @param {Game} game instance of the game class
   */
  constructor(game) {
    /**
     * Instance of the game object
     * @type {Game}
     */
    this.game = game;

    /**
     * An object with all of the bubbles to display
     * @type {Blob[]}
     */
    this.bubbles = {};

    /**
     * Jquery reference to the display bubble for text messages
     * @type {DOMElement}
     */
    this.container = $('#bubbles');
  }

  /**
   * Create a bubble message
   * @param  {Number} id       the id of the bubble message
   * @param  {String} message  contents of the bubble
   * @param  {Number} time     when the bubble will reset
   * @param  {Number} [duration=5000] duration set on the Blob's internal Timer
   * @return {Blob} an instance of the bubble message
   */
  create(id, message, time, duration) {
    if (this.bubbles[id]) {
      this.bubbles[id].reset(time);
      $(`#${id} p`).html(message);
    } else {
      const element = $(`<div id='${id}' class='bubble'>
          <p>${message}</p>
          <div class='bubbleTip'></div>
        </div>`);

      $(element).appendTo(this.container);

      this.bubbles[id] = new Blob(id, time, element, duration);
    }

    return this.bubbles[id];
  }

  /**
   * Set a speech bubble to a specific entity
   * @param {Entity} entity an entity in the game
   * @return null, speech bubble shows in the location of the entity
   */
  setTo(entity) {
    const bubble = this.get(entity.id);

    if (!bubble || !entity) {
      return;
    }

    const scale = this.game.renderer.getDrawingScale();
    const tileSize = 16 * scale;
    const x = (entity.x - this.game.getCamera().x) * scale;
    const width = parseInt($(bubble.element).css('width'), 10) + 24;
    const offset = width / 2 - tileSize / 2;
    const offsetY = 10;
    const y = (entity.y - this.game.getCamera().y) * scale - tileSize * 2 - offsetY;

    $(bubble.element).css(
      'left',
      `${x - offset + (2 + this.game.renderer.scale)}px`,
    );
    $(bubble.element).css('top', `${y}px`);
  }

  /**
   * Update the display of the bubbles (automatically disappear over time)
   * @param {Number} time the current timestamp
   * @return null, deletes expired speech bubbles
   */
  update(time) {
    _.each(this.bubbles, (bubble) => {
      const entity = this.game.entities.get(bubble.id);

      if (entity) {
        this.setTo(entity);
      }

      if (bubble.isOver(time)) {
        bubble.destroy();
        delete this.bubbles[bubble.id];
      }
    });
  }

  /**
   * Fetch a specific bubble
   * @param {Number} id the id of the bubble message
   * @return {Bubble} an instance of the bubble message
   */
  get(id) {
    if (id in this.bubbles) {
      return this.bubbles[id];
    }

    return null;
  }

  /**
   * Destroy all the active bubbles
   * @return null, empty array of bubbles
   */
  clean() {
    _.each(this.bubbles, (bubble) => {
      bubble.destroy();
    });

    this.bubbles = {};
  }

  /**
   * Delete a specific bubble
   * @param {Number} id the id of the bubble message
   * @return null, the bubble is removed from the array
   */
  destroy(id) {
    const bubble = this.get(id);

    if (!bubble) {
      return;
    }

    bubble.destroy();

    delete this.bubbles[id];
  }
}

import $ from 'jquery';
import Modules from '../utils/modules';
import Packets from '../network/packets';

/**
 * Manages the game's chat log window and chat history
 * @class
 */
export default class Chat {
  /**
   * Default constructor
   * @param {Game} game instance of the game class
   */
  constructor(game) {
    /**
     * Instance of the game object
     * @type {Game}
     */
    this.game = game;

    /**
     * Jquery reference to the chat container
     * @type {DOMElement}
     */
    this.chat = $('#chat');

    /**
     * Jquery reference to the chat logs container
     * @type {DOMElement}
     */
    this.log = $('#chatLog');

    /**
     * Jquery reference to the chat input element
     * @type {DOMElement}
     */
    this.input = $('#hud-chat-input');

    /**
     * Jquery reference to the chat button
     * @type {DOMElement}
     */
    this.button = $('#hud-chat');

    /**
     * The visibility of the chat containers
     * @type {Boolean}
     */
    this.visible = false;

    /**
     * The duration before the chat message starts to fade out
     * @type {Number}
     */
    this.fadingDuration = 5000;

    /**
     * A reference to the timeout for the chat message
     * @type {Object}
     */
    this.fadingTimeout = null;

    /**
     * On click handler for the chat button, toggles the visibility
     * @type {Function}
     */
    this.button.click(() => {
      this.button.blur();

      if (this.input.is(':visible')) {
        this.hideInput();
      } else {
        this.toggle();
      }
    });
  }

  /**
  * Add a chat message
  * @param {String} label info in front of the message
  * @param {String} text the message text
  * @param {String} labelColor the color of the label
  * @param {String} textColor the color of the message text
  * @return null
  */
  add(label, text, labelColor, textColor) {
    const styleLabel = labelColor || 'white';
    const styleText = textColor || 'white';

    const element = $(
      `<p>
        <span style="color:${styleLabel}">${label}:</span>
        <span style="color:${styleText}"> ${text}</span>
      </p>`,
    );

    this.showChat();

    if (!this.isActive()) {
      this.hideInput();
    }

    this.hideChat();

    this.log.append(element);
    this.log.scrollTop(99999);
  }

  /**
  * Respond to key enter key presses for chat messages
  * @param {Number} keyCode the code for the key that was pressed
  * @return null
  */
  key(keyCode) {
    switch (keyCode) {
      case Modules.Keys.Enter:
        if (this.input.val() === '') {
          this.toggle();
        } else {
          this.send();
        }

        break;
      default:
        break;
    }
  }

  /**
  * Send chat messages to the server via sockets
  * @return null
  */
  send() {
    this.game.socket.send(Packets.Chat, [this.input.val()]);
    this.toggle();
  }

  /**
  * Toggle if they chat UI is visible or not
  * @return null
  */
  toggle() {
    this.clean();

    if (this.visible && !this.isActive()) this.showInput();
    else if (this.visible) {
      this.hideInput();
      this.hideChat();
    } else {
      this.showChat();
      this.showInput();
    }
  }

  /**
  * Show the chat messages box
  * @return null
  */
  showChat() {
    this.chat.fadeIn('fast');

    this.visible = true;
  }

  /**
  * Show the input box for entering chat messages into
  * @return null
  */
  showInput() {
    this.button.addClass('active');

    this.input.fadeIn('fast');
    this.input.val('');
    this.input.focus();

    this.clean();
  }

  /**
  * Hides the input box for entering chat messages into
  * @return null
  */
  hideChat() {
    if (this.fadingTimeout) {
      clearTimeout(this.fadingTimeout);
      this.fadingTimeout = null;
    }

    this.fadingTimeout = setTimeout(() => {
      if (!this.isActive()) {
        this.chat.fadeOut('slow');

        this.visible = false;
      }
    }, this.fadingDuration);
  }

  /**
  * Hide the chat input boxes
  * @return null
  */
  hideInput() {
    this.button.removeClass('active');

    this.input.val('');
    this.input.fadeOut('fast');
    this.input.blur();

    this.hideChat();
  }

  /**
  * Reset the fade timeout
  * @return null
  */
  clean() {
    clearTimeout(this.fadingTimeout);
    this.fadingTimeout = null;
  }

  /**
  * Returns true if the input box has focus and is currently active
  * or being typed into
  * @return {Boolean}
  */
  isActive() {
    return this.input.is(':focus');
  }
}

import _ from 'underscore';
import Grids from '../renderer/grids';
import Chest from '../entity/objects/chest';
import Player from '../entity/character/player/player';
import Item from '../entity/objects/item';
import Sprites from './sprites';
import Mob from '../entity/character/mob/mob';
import NPC from '../entity/character/npc/npc';
import Projectile from '../entity/objects/projectile';
import Modules from '../utils/modules';
import Packets from '../network/packets';
import log from '../lib/log';

/**
 * Entities in the game:
 * * {@link Character} - parent class for:
 *   * {@link Mob} - game controlled npc or monster that can move around the map
 *   * {@link Npc} - game controlled entity that always stays in one location
 *   * {@link Player} - a user in the game
 * * {@link Chest} - open to reveal an item
 * * {@link Item} - weapons, health, magic, armor
 * * {@link Projectile} - entities that can be thrown (magic, weapons, arrows, mob attacks)
 * @class
 */
export default class Entities {
  /**
  * Default constructor
  * @param {Game} instance of the game
  */
  constructor(game) {
    log.debug('Entities - constructor()');

    /**
    * The instance of the game
    * @type {Game}
    */
    this.game = game;

    /**
    * An instance of the game renderer
    * @type {Renderer}
    */
    this.renderer = game.renderer;

    /**
    * An array of game grid maps
    * @type {Grids}
    */
    this.grids = null;

    /**
    * An array of sprites
    * @type {Sprites}
    */
    this.sprites = null;

    /**
    * An array of an entity type instances
    * @type {Chest|NPC|Item|Mob|Projectile|Player}
    */
    this.entities = {};

    /**
    * Keeps track of entities that need to be cleaned up
    * @type {Chest|NPC|Item|Mob|Projectile|Player}
    */
    this.decrepit = {};
  }

  /**
  * Load the entities in the game for the current game map
  * @return null
  */
  load() {
    log.debug('Entities - load()', this.game.renderer);
    this.game.app.sendStatus('Lots of monsters ahead...');

    if (!this.sprites) {
      log.debug('Entities - load() - no sprites loaded yet', this.game.renderer);
      this.sprites = new Sprites(this.game.renderer);

      this.sprites.onLoadedSprites(() => {
        log.debug('Entities - load() - sprites done loading, loading cursors');
        this.game.input.loadCursors();
        this.game.postLoad();
        this.game.start();
      });
    }

    this.game.app.sendStatus('Yes, you are also a monster...');

    if (!this.grids) {
      log.debug('Entities - load() - no grids loaded yet');
      this.grids = new Grids(this.game.map);
    }
  }

  /**
  * Update the sprites
  * @return null
  */
  update() {
    log.debug('Entities - update()');

    if (this.sprites) {
      this.sprites.updateSprites();
    }
  }

  /**
  * Create a new entity
  * @param {{id:Number, type:'chest'|'npc'|'item'|'mob'|'projectile'|'player'}}
  * info information about the entity we need to create
  * @return null
  */
  create(info) {
    log.debug('Entities - create()', info);

    let entity;

    if (this.isPlayer(info.id)) {
      return;
    }

    switch (info.type) {
      default:
        break;
      case 'chest':
        /**
         * Here we will parse the different types of chests..
         * We can go Dark Souls style and implement mimics
         * the proper way -ahem- TTA V1.0
         */

        const chest = new Chest(info.id, info.name, info.label); // eslint-disable-line
        entity = chest;
        break;

      case 'npc':
        const npc = new NPC(info.id, info.name, info.label); // eslint-disable-line
        entity = npc;
        break;

      case 'item':
        const item = new Item( // eslint-disable-line
          info.id,
          info.name,
          info.label,
          info.count,
          info.ability,
          info.abilityLevel,
        );
        entity = item;
        break;

      case 'mob':
        const mob = new Mob(info.id, info.name, info.label); // eslint-disable-line

        mob.setHitPoints(info.hitPoints);
        mob.setMaxHitPoints(info.maxHitPoints);

        mob.attackRange = info.attackRange;
        mob.level = info.level;

        entity = mob;

        break;

      case 'projectile':
        const attacker = this.get(info.characterId); // eslint-disable-line
        const target = this.get(info.targetId); // eslint-disable-line

        if (!attacker || !target) {
          return;
        }

        attacker.lookAt(target);

        const projectile = new Projectile( // eslint-disable-line
          info.id,
          info.projectileType,
          attacker,
          info.name,
        );

        projectile.setStart(attacker.x, attacker.y);
        projectile.setTarget(target);

        projectile.setSprite(this.getSprite(projectile.name));
        projectile.setAnimation('travel', projectile.getSpeed());

        projectile.angled = true;
        projectile.type = info.type;

        /**
         * Move this into the external overall function
         */

        projectile.onImpact(() => {
          /**
           * The data in the projectile is only for rendering purposes
           * there is nothing you can change for the actual damage output here.
           */

          if (this.isPlayer(projectile.owner.id) || this.isPlayer(target.id)) {
            this.game.socket.send(Packets.Projectile, [
              Packets.ProjectileOpcode.Impact,
              info.id,
              target.id,
            ]);
          }

          if (info.hitType === Modules.Hits.Explosive) {
            target.explosion = true;
          }

          this.game.info.create(
            Modules.Hits.Damage,
            [info.damage, this.isPlayer(target.id)],
            target.x,
            target.y,
          );

          target.triggerHealthBar();

          this.unregisterPosition(projectile);
          delete this.entities[projectile.getId()];
        });

        this.addEntity(projectile);

        attacker.performAction(attacker.orientation, Modules.Actions.Attack);
        attacker.triggerHealthBar();
        return;

      case 'player':
        const player = new Player(); // eslint-disable-line

        player.setId(info.id);
        player.setName(info.name);
        player.setGridPosition(info.x, info.y);

        player.rights = info.rights;
        player.level = info.level;
        player.pvp = info.pvp;
        player.pvpKills = info.pvpKills;
        player.pvpDeaths = info.pvpDeaths;
        player.type = info.type;

        const hitPointsData = info.hitPoints; // eslint-disable-line
        const manaData = info.mana; // eslint-disable-line

        player.setHitPoints(hitPointsData[0]);
        player.setMaxHitPoints(hitPointsData[1]);

        player.setMana(manaData[0]);
        player.setMaxMana(manaData[1]);

        player.setSprite(this.getSprite(info.armour[1]));
        player.idle();

        player.setEquipment(Modules.Equipment.Armour, info.armour);
        player.setEquipment(Modules.Equipment.Weapon, info.weapon);
        player.setEquipment(Modules.Equipment.Pendant, info.pendant);
        player.setEquipment(Modules.Equipment.Ring, info.ring);
        player.setEquipment(Modules.Equipment.Boots, info.boots);

        player.loadHandler(this.game);

        this.addEntity(player);
        return;
    }

    // if we didn't load an entity for any reason quit trying while we're ahead
    if (!entity) {
      return;
    }

    entity.setGridPosition(info.x, info.y);
    entity.setName(info.name);

    entity.setSprite(
      this.getSprite(
        info.type === 'item'
          ? `item-${info.name}`
          : info.name,
      ),
    );

    entity.idle();
    entity.type = info.type;

    this.addEntity(entity);

    if (info.type !== 'item' && entity.handler) {
      entity.handler.setGame(this.game);
      entity.handler.load();
    }
  }

  /**
  * Checks whether or not this entity ID is a player
  * @param {Number} id the id of the entity
  * @return {Boolean}
  */
  isPlayer(id) {
    log.debug('Entities - isPlayer()', id);
    return this.game.player.id === id;
  }

  /**
  * Returns the entity object based off the ID if it exists, otherwise returns null
  * @param {Number} id the id of the entity
  * @return {Chest|NPC|Item|Mob|Projectile|Player|null}
  */
  get(id) {
    log.debug('Entities - get()', id);

    if (id in this.entities) {
      return this.entities[id];
    }

    return null;
  }

  /**
  * Check to see if an entity with this ID exists or not
  * @param {Number} id the id of the entity
  * @return {Boolean} true if it exists, otherwise false
  */
  exists(id) {
    log.debug('Entities - exists()', id);
    return id in this.entities;
  }

  /**
  * Removes players from the game map, will only work for entities of type player
  * @param {{id:Number, type:String}} exception entitys (players) to keep on the game map
  * @return null
  */
  clearPlayers(exception) {
    log.debug('Entities - clearPlayers()', exception);
    _.each(this.entities, (entity) => {
      if (entity.id !== exception.id && entity.type === 'player') {
        this.grids.removeFromRenderingGrid(
          entity,
          entity.gridX,
          entity.gridY,
        );

        this.grids.removeFromPathingGrid(entity.gridX, entity.gridY);

        delete this.entities[entity.id];
      }
    });

    this.grids.resetPathingGrid();
  }

  /**
  * Add a new entity to the map grid
  * @param {Chest|NPC|Item|Mob|Projectile|Player} entity the entity to add to the game
  * @return null
  */
  addEntity(entity) {
    log.debug('Entities - addEntity()', entity);

    if (this.entities[entity.id]) {
      return;
    }

    this.entities[entity.id] = entity;
    this.registerPosition(entity);

    if (
      !(entity instanceof Item && entity.dropped)
      && !this.renderer.isPortableDevice()
    ) {
      entity.fadeIn(this.game.time);
    }
  }

  /**
  * Remove an item from the game
  * @param {{Chest|NPC|Item|Mob|Projectile}} item the item to remove from the map grid
  * @return null
  */
  removeItem(item) {
    log.debug('Entities - removeItem()');

    if (!item) {
      return;
    }

    this.grids.removeFromItemGrid(item, item.gridX, item.gridY);
    this.grids.removeFromRenderingGrid(item, item.gridX, item.gridY);

    delete this.entities[item.id];
  }

  /**
  * Figure out which part of the grid to add an entity to
  * @param {Chest|NPC|Item|Mob|Player} entity an entity in the game
  * @return null
  */
  registerPosition(entity) {
    log.debug('Entities - registerPosition()', entity);

    if (!entity) {
      return;
    }

    if (
      entity.type === 'player'
      || entity.type === 'mob'
      || entity.type === 'npc'
      || entity.type === 'chest'
    ) {
      this.grids.addToEntityGrid(entity, entity.gridX, entity.gridY);

      if (entity.type !== 'player' || entity.nonPathable) {
        this.grids.addToPathingGrid(entity.gridX, entity.gridY);
      }
    }

    if (entity.type === 'item') {
      this.grids.addToItemGrid(entity, entity.gridX, entity.gridY);
    }

    this.grids.addToRenderingGrid(entity, entity.gridX, entity.gridY);
  }

  /**
  * Register this entity on the grid twice
  * @param {Chest|NPC|Item|Mob|Projectile|Player} entity the entity to add to the game map again
  * @return null
  */
  registerDuality(entity) {
    log.debug('Entities - registerDuality()', entity);

    if (!entity) {
      return;
    }

    this.grids.entityGrid[entity.gridY][entity.gridX][entity.id] = entity;

    this.grids.addToRenderingGrid(entity, entity.gridX, entity.gridY);

    if (entity.nextGridX > -1 && entity.nextGridY > -1) {
      this.grids.entityGrid[entity.nextGridY][entity.nextGridX][
        entity.id
      ] = entity;

      if (!(entity instanceof Player)) {
        this.grids.pathingGrid[entity.nextGridY][entity.nextGridX] = 1;
      }
    }
  }

  /**
  * Remove this entity from the game map
  * @param {Chest|NPC|Item|Mob|Projectile|Player} entity the entity to remove from the grid
  * @return null
  */
  unregisterPosition(entity) {
    log.debug('Entities - unregisterPosition()', entity);

    if (!entity) {
      return;
    }

    this.grids.removeEntity(entity);
  }

  /**
  * Return all the sprites with the given name
  * @param {String} name the name of the sprite
  * @return {Chest|NPC|Item|Mob|Projectile|Player}
  */
  getSprite(name) {
    log.debug('Entities - getSprite()', name);
    return this.sprites.sprites[name];
  }

  /**
  * Return all entities
  */
  getAll() {
    log.debug('Entities - getAll()');
    return this.entities;
  }

  /**
  * Apply a callback for every entity in the array
  * @param {Function} callback the function to apply, the entity is passed into it
  * @return null
  */
  forEachEntity(callback) {
    // log.debug('Entities - forEachEntity()', callback);
    _.each(this.entities, (entity) => {
      callback(entity);
    });
  }

  /**
  * Apply a callback for every entity in the radius of an area on the map grid
  * as long as the radius is not out of bounds in the grid
  * @param {Number} x the x coordinate center of the circle radius
  * @param {Number} y the y coordinate center of the circle radius
  * @param {Number} radius the the distance to spread out from the center of the circle point
  * @param {Function} callback the function to apply, the entity is passed into it
  * @return null
  */
  forEachEntityAround(x, y, radius, callback) {
    log.debug('Entities - forEachEntityAround()', x, y, radius, callback);

    for (let i = x - radius, maxI = x + radius; i <= maxI; i += 1) {
      for (let j = y - radius, maxJ = y + radius; j <= maxJ; j += 1) {
        if (!this.map.isOutOfBounds(i, j)) {
          _.each(this.grids.renderingGrid[j][i], (entity) => {
            callback(entity);
          });
        }
      }
    }
  }
}

import _ from 'underscore';
import Queue from '../utils/queue';
import Splat from '../renderer/infos/splat';
import Modules from '../utils/modules';
import {
  isInt,
} from '../utils/util';

/**
 * Displays additional information on the screen as the game
 * is running. For instance, missing hits on another player,
 * regenerating health, and damage done with a {@link Splat}
 * @class
 */
export default class Info {
  /**
   * Default constructor
   * @param {Game} game instance of the game
   */
  constructor(game) {
    /**
    * An instance of the game
    * @type {Game}
    */
    this.game = game;

    /**
    * A collection of all the info entities
    * @type {Object}
    */
    this.infos = {};

    /**
    * A queue to destroy the entities over time
    * @type {Queue}
    */
    this.destroyQueue = new Queue();
  }

  /**
   * Create the informational entity
   * @param  {Modules} type Hits (damage, stun, critical, heal, mana, experience, levelup)
   * @param  {Object}  data  Data about the type of hit
   * @param  {Number}  x     X coordinate
   * @param  {Number}  y     Y coordinate
   */
  create(type, data, x, y) {
    switch (type) {
      case Modules.Hits.Damage:
      case Modules.Hits.Stun:
      case Modules.Hits.Critical:
        this.hit(type, data, x, y);
        break;
      case Modules.Hits.Heal:
      case Modules.Hits.Mana:
      case Modules.Hits.Experience:
        this.regenerate(type, data, x, y);
        break;
      case Modules.Hits.LevelUp:
        this.levelup(type, x, y);
        break;
      default:
        break;
    }
  }

  /**
   * The hit has made contact with another entity
   * @param  {Modules} type The Hits type of {@link Splat}
   * @param  {Object}  data Damage and whether or not this isTarget
   * @param  {Number}  x    X coordinate
   * @param  {Number}  y    Y coordinate
   */
  hit(type, data, x, y) {
    let damage = data.shift();
    const isTarget = data.shift();
    const dId = this.generateId(damage, x, y);

    if (damage < 1 || !isInt(damage)) {
      damage = 'MISS';
    }

    const hitSplat = new Splat(dId, type, damage, x, y, false);

    const dColour = isTarget
      ? Modules.DamageColours.received
      : Modules.DamageColours.inflicted;

    hitSplat.setColours(dColour.fill, dColour.stroke);

    this.addInfo(hitSplat);
  }

  /**
   * Renerage health
   * @param  {Modules} type The Hits type of {@link Splat}
   * @param  {Object}  data Amount of health regeneration
   * @param  {Number}  x    X coordinate
   * @param  {Number}  y    Y coordinate
   */
  regenerate(type, data, x, y) {
    const amount = data.shift();
    const id = this.generateId(amount, x, y);
    let text = '+';
    let colour = null;

    if (amount < 1 || !isInt(amount)) {
      return;
    }

    if (type !== Modules.Hits.Experience) {
      text = '++';
    }

    const splat = new Splat(id, type, text + amount, x, y, false);

    if (type === Modules.Hits.Heal) {
      colour = Modules.DamageColours.healed;
    } else if (type === Modules.Hits.Mana) {
      colour = Modules.DamageColours.mana;
    } else if (type === Modules.Hits.Experience) {
      colour = Modules.DamageColours.exp;
    }

    splat.setColours(colour.fill, colour.stroke);
    this.addInfo(splat);
  }

  /**
   * Level up
   * @param  {Modules} type The Hits type
   * @param  {Number}  x    X coordinate
   * @param  {Number}  y    Y coordinate
   */
  levelup(type, x, y) {
    const lId = this.generateId('-1', x, y);
    const levelSplat = new Splat(lId, type, 'Level Up!', x, y, false);
    const lColour = Modules.DamageColours.exp;

    levelSplat.setColours(lColour.fill, lColour.stroke);
    this.addInfo(levelSplat);
  }

  /**
   * Returns the count of the number of info objects currently active
   * @return {Number} number of active info objects
   */
  getCount() {
    return Object.keys(this.infos).length;
  }

  /**
   * Add an info object to the destroyQueue
   * @param {Object} info An info object
   */
  addInfo(info) {
    this.infos[info.id] = info;

    info.onDestroy((id) => {
      this.destroyQueue.add(id);
    });
  }

  /**
   * Update each info object over time and destroy any in the destroyQueue
   * @param  {Number} time set the latest update time for each info
   */
  update(time) {
    this.forEachInfo((info) => {
      info.update(time);
    });

    this.destroyQueue.forEachQueue((id) => {
      delete this.infos[id];
    });

    this.destroyQueue.reset();
  }

  /**
   * Loop through each info and pass them to a callback
   * @param  {Function} callback Callback function
   */
  forEachInfo(callback) {
    _.each(this.infos, (info) => {
      callback(info);
    });
  }

  /**
   * Generate an ID for an info based on the game type, info Module.Hits and x,y coordinates
   * @param  {Modules} info The Hits type for this info entity
   * @param  {Number}  x    X coordinate
   * @param  {Number}  y    Y coordinate
   * @return {Number}  A generated ID number
   */
  generateId(info, x, y) {
    return `${this.game.time}${Math.abs(info)}${x}${y}`;
  }
}

