      } else if (achievement.progress > 9998) {
        name.text(achievement.name);
        name.css('background', 'rgba(10, 255, 10, 0.3)');
      }

      if (achievement.finished) {
        finishedAchievements += 1;
      }

      item.append(name);

      const listItem = $('<li></li>');

      listItem.append(item);

      this.achievementsList.append(listItem);
    });

    _.each(quests, (quest) => {
      const item = this.getItem(true, quest.id);
      const name = this.getName(true, quest.id);

      name.text(quest.name);
      name.css('background', 'rgba(255, 10, 10, 0.3)');

      if (quest.stage > 0 && quest.stage < 9999) {
        name.css('background', 'rgba(255, 255, 10, 0.4)');
      } else if (quest.stage > 9998) {
        name.css('background', 'rgba(10, 255, 10, 0.3)');
      }

      if (quest.finished) {
        finishedQuests += 1;
      }

      item.append(name);

      const listItem = $('<li></li>');

      listItem.append(item);

      this.questList.append(listItem);
    });

    this.achievementsCount.html(
      `${finishedAchievements}/${achievements.length}`,
    );
    this.questCount.html(`${finishedQuests}/${quests.length}`);
  }

  progress(info) {
    const item = info.isQuest
      ? this.getQuest(info.id)
      : this.getAchievement(info.id);

    if (!item) return;

    const name = item.find(
      `${info.isQuest ? '#quest' : '#achievement'}${info.id}name`,
    );

    if (!name) return;

    if (!info.isQuest && info.count > 2) name.text(`${info.name} ${info.progress}/${info.count - 1}`);

    name.css('background', 'rgba(255, 255, 10, 0.4)');
  }

  finish(info) {
    const item = info.isQuest
      ? this.getQuest(info.id)
      : this.getAchievement(info.id);

    if (!item) return;

    const name = item.find(
      `${info.isQuest ? '#quest' : '#achievement'}${info.id}name`,
    );

    if (!name) return;

    if (!info.isQuest) name.text(info.name);

    name.css('background', 'rgba(10, 255, 10, 0.3)');
  }

  getQuest(id) {
    return $(this.questList.find('li')[id]).find(`#quest${id}`);
  }

  getAchievement(id) {
    return $(this.achievementsList.find('li')[id]).find(`#achievement${id}`);
  }

  /**
   * Might as well properly organize them based
   * on their type of item and id (index).
   */

  getItem(isQuest, id) {
    return $(
      `<div id="${isQuest ? 'quest' : 'achievement'}${id}" class="questItem"></div>`,
    );
  }

  getName(isQuest, id) {
    return $(
      `<div id="${isQuest ? 'quest' : 'achievement'}${id}name" class="questName"></div>`,
    );
  }
}

import $ from 'jquery';
import Page from '../page';

export default class Settings extends Page {
  constructor(game) {
    super('#settingsPage');
    this.game = game;
    this.audio = game.audio;
    this.storage = game.storage;
    this.renderer = game.renderer;
    this.camera = game.renderer.camera;
    this.button = $('#settingsButton');
    this.volume = $('#volume');
    this.sfx = $('#sfx');
    this.brightness = $('#brightness');
    this.intensity = $('#intensity');
    this.info = $('#info');
    this.soundCheck = $('#soundCheck');
    this.cameraCheck = $('#cameraCheck');
    this.debugCheck = $('#debugCheck');
    this.centreCheck = $('#centreCheck');
    this.nameCheck = $('#nameCheck');
    this.levelCheck = $('#levelCheck');
    this.loaded = false;
    this.load();
  }

  load() {
    if (this.loaded) {
      return;
    }

    this.volume.val(this.getMusicLevel());
    this.sfx.val(this.getSFXLevel());
    this.brightness.val(this.getBrightness());
    this.intensity.val(this.getIntensity());

    this.game.app.updateRange(this.volume);
    this.game.app.updateRange(this.sfx);
    this.game.app.updateRange(this.brightness);
    this.game.app.updateRange(this.intensity);

    this.renderer.adjustBrightness(this.getBrightness());

    this.button.click(() => {
      this.game.interface.hideAll();
      this.button.toggleClass('active');

      if (this.isVisible()) {
        this.hide();
      } else {
        this.show();
      }
    });

    this.volume.on('input', () => {
      if (this.audio.song) {
        this.audio.song.volume = this.value / 100;
      }
    });

    this.brightness.on('input', () => {
      this.renderer.adjustBrightness(this.value);
    });

    this.volume.change(() => {
      this.setMusicLevel(this.value);
    });

    this.sfx.change(() => {
      this.setSFXLevel(this.value);
    });

    this.brightness.change(() => {
      this.setBrightness(this.value);
    });

    this.intensity.change(() => {
      this.setIntensity(this.value);
    });

    this.soundCheck.click(() => {
      const isActive = this.soundCheck.hasClass('active');

      this.setSound(!isActive);

      if (isActive) {
        this.audio.reset(this.audio.song);
        this.audio.song = null;
        this.soundCheck.removeClass('active');
      } else {
        this.audio.update();
        this.soundCheck.addClass('active');
      }
    });

    this.cameraCheck.click(() => {
      const active = this.cameraCheck.hasClass('active');

      if (active) {
        this.renderer.camera.decenter();
      } else {
        this.renderer.camera.center();
      }
      this.cameraCheck.toggleClass('active');
      this.setCamera(!active);
    });

    this.debugCheck.click(() => {
      const active = this.debugCheck.hasClass('active');
      this.debugCheck.toggleClass('active');
      this.renderer.debugging = !active;
      this.setDebug(!active);
    });

    this.centreCheck.click(() => {
      const active = this.centreCheck.hasClass('active');
      this.centreCheck.toggleClass('active');
      this.renderer.autoCentre = !active;
      this.setCentre(!active);
    });

    this.nameCheck.click(() => {
      const active = this.nameCheck.hasClass('active');
      this.nameCheck.toggleClass('active');
      this.renderer.drawNames = !active;
      this.setName(!active);
    });

    this.levelCheck.click(() => {
      const active = this.levelCheck.hasClass('active');
      this.levelCheck.toggleClass('active');
      this.renderer.drawLevels = !active;
      this.setName(!active);
    });

    if (this.getSound()) {
      this.soundCheck.addClass('active');
    }

    if (this.getCamera()) {
      this.cameraCheck.addClass('active');
    } else {
      this.camera.centered = false;
      this.renderer.verifyCentration();
    }

    if (this.getDebug()) {
      this.debugCheck.addClass('active');
      this.renderer.debugging = true;
    }

    if (this.getCentreCap()) {
      this.centreCheck.addClass('active');
    }

    if (this.getName()) {
      this.nameCheck.addClass('active');
    } else {
      this.renderer.drawNames = false;
    }

    if (this.getLevel()) {
      this.levelCheck.addClass('active');
    } else {
      this.renderer.drawLevels = false;
    }

    this.loaded = true;
  }

  show() {
    this.body.fadeIn('slow');
  }

  hide() {
    this.body.fadeOut('fast');
  }

  setMusicLevel(musicLevel) {
    this.storage.data.settings.music = musicLevel;
    this.storage.save();
  }

  setSFXLevel(sfxLevel) {
    this.storage.data.settings.sfx = sfxLevel;
    this.storage.save();
  }

  setBrightness(brightness) {
    this.storage.data.settings.brightness = brightness;
    this.storage.save();
  }

  setIntensity(intensity) {
    const converted = (10 - intensity) / 10;
    this.storage.data.intensity = converted;
    this.storage.save();
  }

  setSound(state) {
    this.storage.data.settings.soundEnabled = state;
    this.storage.save();
  }

  setCamera(state) {
    this.storage.data.settings.centerCamera = state;
    this.storage.save();
  }

  setDebug(state) {
    this.storage.data.settings.debug = state;
    this.storage.save();
  }

  setCentre(state) {
    this.storage.data.settings.autoCentre = state;
    this.storage.save();
  }

  setName(state) {
    this.storage.data.settings.showNames = state;
    this.storage.save();
  }

  setLevel(state) {
    this.storage.data.settings.showLevels = state;
    this.storage.save();
  }

  getMusicLevel() {
    return this.storage.data.settings.music;
  }

  getSFXLevel() {
    return this.storage.data.settings.sfx;
  }

  getBrightness() {
    return this.storage.data.settings.brightness;
  }

  getIntensity() {
    return (1 - this.storage.data.intensity) * 10;
  }

  getSound() {
    return this.storage.data.settings.soundEnabled;
  }

  getCamera() {
    return this.storage.data.settings.centerCamera;
  }

  getDebug() {
    return this.storage.data.settings.debug;
  }

  getCentreCap() {
    return this.storage.data.settings.autoCentre;
  }

  getName() {
    return this.storage.data.settings.showNames;
  }

  getLevel() {
    return this.storage.data.settings.showLevels;
  }

  isVisible() {
    return this.body.css('display') === 'block';
  }
}

import $ from 'jquery';
import _ from 'underscore';
import Page from '../page';
import Packets from '../../../network/packets';

export default class State extends Page {
  constructor(game) {
    super('#statePage');

    this.game = game;
    this.player = game.player;

    this.name = $('#profileName');
    this.level = $('#profileLevel');
    this.experience = $('#profileExperience');

    this.weaponSlot = $('#weaponSlot');
    this.armourSlot = $('#armourSlot');
    this.pendantSlot = $('#pendantSlot');
    this.ringSlot = $('#ringSlot');
    this.bootsSlot = $('#bootsSlot');

    this.slots = [
      this.weaponSlot,
      this.armourSlot,
      this.pendantSlot,
      this.ringSlot,
      this.bootsSlot,
    ];

    this.loaded = false;

    this.load();
  }

  resize() {
    this.loadSlots();
  }

  load() {
    if (!this.game.player.armour) {
      return;
    }

    this.name.text(this.player.username);
    this.level.text(this.player.level);
    this.experience.text(this.player.experience);

    this.loadSlots();

    this.loaded = true;

    this.weaponSlot.click(() => {
      this.game.socket.send(Packets.Equipment, [
        Packets.EquipmentOpcode.Unequip,
        'weapon',
      ]);
    });

    this.armourSlot.click(() => {
      this.game.socket.send(Packets.Equipment, [
        Packets.EquipmentOpcode.Unequip,
        'armour',
      ]);
    });

    this.pendantSlot.click(() => {
      this.game.socket.send(Packets.Equipment, [
        Packets.EquipmentOpcode.Unequip,
        'pendant',
      ]);
    });

    this.ringSlot.click(() => {
      this.game.socket.send(Packets.Equipment, [
        Packets.EquipmentOpcode.Unequip,
        'ring',
      ]);
    });

    this.bootsSlot.click(() => {
      this.game.socket.send(Packets.Equipment, [
        Packets.EquipmentOpcode.Unequip,
        'boots',
      ]);
    });
  }

  loadSlots() {
    this.weaponSlot.css(
      'background-image',
      this.getImageFormat(this.getScale(), this.player.weapon.name),
    );
    this.armourSlot.css(
      'background-image',
      this.getImageFormat(this.getScale(), this.player.armour.name),
    );
    this.pendantSlot.css(
      'background-image',
      this.getImageFormat(this.getScale(), this.player.pendant.name),
    );
    this.ringSlot.css(
      'background-image',
      this.getImageFormat(this.getScale(), this.player.ring.name),
    );
    this.bootsSlot.css(
      'background-image',
      this.getImageFormat(this.getScale(), this.player.boots.name),
    );

    if (this.game.getScaleFactor() === 1) {
      this.forEachSlot((slot) => {
        slot.css('background-size', '600%');
      });
    }
  }

  update() {
    this.level.text(this.player.level);
    this.experience.text(this.player.experience);
    this.loadSlots();
  }

  forEachSlot(callback) {
    _.each(this.slots, (slot) => {
      callback(slot);
    });
  }

  getScale() {
    return this.game.renderer.getDrawingScale();
  }
}

import $ from 'jquery';
import _ from 'underscore';
import State from './pages/state';
import Ability from './pages/ability';
import Settings from './pages/settings';
import Quest from './pages/quest';
import Packets from '../../network/packets';

export default class Profile {
  constructor(game) {
    this.game = game;
    this.body = $('#profileDialog');
    this.button = $('#profileButton');
    this.next = $('#next');
    this.previous = $('#previous');
    this.activePage = null;
    this.activeIndex = 0;
    this.pages = [];
    this.load();
  }

  load() {
    this.button.click(() => {
      this.game.interface.hideAll();
      this.settings.hide();

      if (this.isVisible()) {
        this.hide();
        this.button.removeClass('active');
      } else {
        this.show();
        this.button.addClass('active');
      }

      if (!this.activePage.loaded) {
        this.activePage.load();
      }

      this.game.socket.send(Packets.Click, [
        'profile',
        this.button.hasClass('active'),
      ]);
    });

    this.next.click(() => {
      if (this.activeIndex + 1 < this.pages.length) this.setPage(this.activeIndex + 1);
      else this.next.removeClass('enabled');
    });

    this.previous.click(() => {
      if (this.activeIndex > 0) this.setPage(this.activeIndex - 1);
      else this.previous.removeClass('enabled');
    });

    this.state = new State(this.game);
    this.ability = new Ability(this.game);
    this.settings = new Settings(this.game);
    this.quests = new Quest(this.game);
    this.pages.push(this.state, this.quests, this.ability);
    this.activePage = this.state;

    if (this.activeIndex === 0 && this.activeIndex !== this.pages.length) {
      this.next.addClass('enabled');
    }
  }

  update() {
    _.each(this.pages, (page) => {
      page.update();
    });
  }

  resize() {
    _.each(this.pages, (page) => {
      page.resize();
    });
  }

  setPage(index) {
    const page = this.pages[index];

    this.clear();

    if (page.isVisible()) {
      return;
    }

    this.activePage = page;
    this.activeIndex = index;

    if (this.activeIndex + 1 === this.pages.length) {
      this.next.removeClass('enabled');
    } else if (this.activeIndex === 0) {
      this.previous.removeClass('enabled');
    } else {
      this.previous.addClass('enabled');
      this.next.addClass('enabled');
    }

    page.show();
  }

  show() {
    this.body.fadeIn('slow');
    this.button.addClass('active');
  }

  hide() {
    this.body.fadeOut('fast');
    this.button.removeClass('active');

    if (this.settings) {
      this.settings.hide();
    }
  }

  isVisible() {
    return this.body.css('display') === 'block';
  }

  clear() {
    if (this.activePage) {
      this.activePage.hide();
    }
  }

  getScale() {
    return this.game.getScaleFactor();
  }
}

import $ from 'jquery';
import Packets from '../network/packets';

export default class Warp {
  constructor(game) {
    this.game = game;
    this.mapFrame = $('#mapFrame');
    this.warp = $('#hud-world');
    this.close = $('#closeMapFrame');
    this.warpCount = 0;
    this.load();
  }

  load() {
    this.warp.click(() => {
      this.toggle();
    });

    this.close.click(() => {
      this.hide();
    });

    for (let i = 1; i < 7; i += 1) {
      const warp = this.mapFrame.find(`#warp${i}`);

      if (warp) {
        warp.click((event) => {
          this.hide();

          this.game.socket.send(Packets.Warp, [
            event.currentTarget.id.substring(4),
          ]);
        });
      }
    }
  }

  /**
   * Just so it fades out nicely.
   */
  toggle() {
    if (this.isVisible()) {
      this.hide();
    } else {
      this.display();
    }
  }

  getScale() {
    return this.game.getScaleFactor();
  }

  isVisible() {
    return this.mapFrame.css('display') === 'block';
  }

  display() {
    this.mapFrame.fadeIn('slow');
  }

  hide() {
    this.mapFrame.fadeOut('fast');
  }
}

/**
* A* (A-Star) algorithm for a path finder
* @author  Andrea Giammarchi
* @license Mit Style License
*/
const Astar = () => (function () {
  function diagonalSuccessors(
    $N,
    $S,
    $E,
    $W,
    N,
    S,
    E,
    W,
    grid,
    rows,
    cols,
    result,
    i,
  ) {
    if ($N) {
      $E && !grid[N][E] && (result[i++] = { x: E, y: N });
      $W && !grid[N][W] && (result[i++] = { x: W, y: N });
    }
    if ($S) {
      $E && !grid[S][E] && (result[i++] = { x: E, y: S });
      $W && !grid[S][W] && (result[i++] = { x: W, y: S });
    }
    return result;
  }

  function diagonalSuccessorsFree(
    $N,
    $S,
    $E,
    $W,
    N,
    S,
    E,
    W,
    grid,
    rows,
    cols,
    result,
    i,
  ) {
    $N = N > -1;
    $S = S < rows;
    $E = E < cols;
    $W = W > -1;
    if ($E) {
      $N && !grid[N][E] && (result[i++] = { x: E, y: N });
      $S && !grid[S][E] && (result[i++] = { x: E, y: S });
    }
    if ($W) {
      $N && !grid[N][W] && (result[i++] = { x: W, y: N });
      $S && !grid[S][W] && (result[i++] = { x: W, y: S });
    }
    return result;
  }

  function nothingToDo(
    $N,
    $S,
    $E,
    $W,
    N,
    S,
    E,
    W,
    grid,
    rows,
    cols,
    result,
    i,
  ) {
    return result;
  }

  function successors(find, x, y, grid, rows, cols) {
    const N = y - 1;
    const S = y + 1;
    const E = x + 1;
    const W = x - 1;
    const $N = N > -1 && !grid[N][x];
    const $S = S < rows && !grid[S][x];
    const $E = E < cols && !grid[y][E];
    const $W = W > -1 && !grid[y][W];
    const result = [];
    let i = 0;
    $N && (result[i++] = { x, y: N });
    $E && (result[i++] = { x: E, y });
    $S && (result[i++] = { x, y: S });
    $W && (result[i++] = { x: W, y });
    return find($N, $S, $E, $W, N, S, E, W, grid, rows, cols, result, i);
  }

  function diagonal(start, end, f1, f2) {
    return f2(f1(start.x - end.x), f1(start.y - end.y));
  }

  function euclidean(start, end, f1, f2) {
    const x = start.x - end.x;
    const y = start.y - end.y;
    return f2(x * x + y * y);
  }

  function manhattan(start, end, f1, f2) {
    return f1(start.x - end.x) + f1(start.y - end.y);
  }

  function AStar(grid, start, end, f) {
    const cols = grid[0].length;
    const rows = grid.length;
    const limit = cols * rows;
    const f1 = Math.abs;
    let f2 = Math.max;
    const list = {};
    const result = [];
    const open = [
      {
        x: start[0], y: start[1], f: 0, g: 0, v: start[0] + start[1] * cols,
      },
    ];
    let length = 1;
    let adj;
    let distance;
    let find;
    let i;
    let j;
    let max;
    let min;
    let current;
    let next;
    end = { x: end[0], y: end[1], v: end[0] + end[1] * cols };
    switch (f) {
      case 'Diagonal':
      case 'Euclidean':
        find = diagonalSuccessors;
      case 'DiagonalFree':
        distance = diagonal;
        break;

      case 'EuclideanFree':
        f2 = Math.sqrt;
        distance = euclidean;
        break;
      default:
        distance = manhattan;
        find = nothingToDo;
        break;
    }
    find || (find = diagonalSuccessorsFree);
    do {
      max = limit;
      min = 0;
      for (i = 0; i < length; ++i) {
        if ((f = open[i].f) < max) {
          max = f;
          min = i;
        }
      }

      current = open.splice(min, 1)[0];
      if (current.v !== end.v) {
        --length;
        next = successors(find, current.x, current.y, grid, rows, cols);
        for (i = 0, j = next.length; i < j; ++i) {
          (adj = next[i]).p = current;
          adj.f = adj.g = 0;
          adj.v = adj.x + adj.y * cols;
          if (!(adj.v in list)) {
            adj.f = (adj.g = current.g + distance(adj, current, f1, f2))
                + distance(adj, end, f1, f2);
            open[length++] = adj;
            list[adj.v] = 1;
          }
        }
      } else {
        i = length = 0;
        do {
          result[i++] = [current.x, current.y];
        } while ((current = current.p));
        result.reverse();
      }
    } while (length);

    return result;
  }

  return AStar;
}());

export default Astar();

/* global window, printStackTrace */
/* eslint-disable */
class Logger {

  /**
   * Default constructor
   * @param {String|null} level debug|info|error
   */
  constructor(level) {
    this.level = (window && window.debugLevel) || level;
    this.classMap = {};

    if (["debug", "info", "prod"].includes(this.level)) {
      console.log("%c    ", "background-image:url('https://raw.githubusercontent.com/design1online/WTF-Adventure/master/assets/img/wtflogo.png');background-repeat:no-repeat;background-position:center center;line-height:.85em;background-size:100% 100%;font-size:200px");
    }
  }

  /**
   * Generate a random color within the acceptable 256 color range
   * @return {String} hexcode with a # prepended on it
   */
  randomColor() {
    let color = (Math.floor(Math.random() * 16777215)).toString(16);
    return `#${this.padEnd(color, 6, '0')}`;
  }

  /**
   * Pads the end of a string with the value if the String.prototype doesn't exist
   * This is particularly for CircleCI which keeps failing :( and always uses the
   * proptotype if it exists
   * @param  {String} str          a string (will be cast to be sure)
   * @param  {Number} targetLength how long the final string should be
   * @param  {String} val          what to pad the string with, defaults to space
   * @return {String}              returns the string at targetLength with val padded on the end
   */
  padEnd(str, targetLength, val = ' ') {
   str = String(str);
   targetLength = parseInt(targetLength, 10) || 0;

   if (str.length >= targetLength) {
     return str;
   }

   if (String.prototype.padEnd) {
     return str.padEnd(targetLength);
   }
   targetLength -= str.length;
   return str + val.repeat(targetLength);
 }

  /**
   * Figure out whether to return white or black text color for the
   * given background colorMatch
   * @param  {String} color the hexcode color without a # mark in front of it
   * @return {String} #000 for light colors and #fff for dark colors
   */
  lightOrDark(color) {

    let r;
    let g;
    let b;
    let hsp;
    let colorMatch;

    // Check the format of the color, HEX or RGB?
    if (color.match(/^rgb/)) {
      // If HEX --> store the red, green, blue values in separate variables
      colorMatch = color.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*(\d+(?:\.\d+)?))?\)$/);
      [r, b, g] = colorMatch;
    } else {
      // If RGB --> Convert it to HEX: http://gist.github.com/983661
      color = +(`0x${color.slice(1).replace(
        color.length < 5 && /./g, '$&$&',
      )}`);

      r = color >> 16;
      g = color >> 8 & 255;
      b = color & 255;
    }

    // HSP (Highly Sensitive Poo) equation from http://alienryderflex.com/hsp.html
    hsp = Math.sqrt(
      0.299 * (r * r)
    + 0.587 * (g * g)
    + 0.114 * (b * b),
    );

    // Using the HSP value, determine whether the color is light or dark
    if (hsp > 150) {
      return '#000';
    }

    return '#fff';
  }

  /**
   * Given the name of the class this is for return
   * the previously associated color or associate a random
   * color to this class name
   * @param  {String} className the name of the class this log message is for
   * @return {{bgcolor:String, textcolor:String}} the background and text color for this
   *  class name
   */
  getColors(className) {
    let bgcolor;
    let textcolor;

    if (bgcolor = this.classMap[className]) {
      textcolor = this.lightOrDark(bgcolor.replace('#', ''));
    } else {
      bgcolor = this.randomColor();
      textcolor = this.lightOrDark(bgcolor.replace('#', ''));

      // set the color in the classMap
      this.classMap[className] = bgcolor;
    }

    return { bgcolor, textcolor };
  }

  /**
   * Helpter function format the console.log message
   * @param  {String} message the first variable passed to the console.log, generally a message
   *  formatted as `ClassName - propertyFunction() - optional additional message`
   * @param  {Object} rest an array of the rest of the variables passed to the console.log, typically class
   *  instances or variables in the file used for debugging
   */
  consoleLog(message, rest) {
    const [className, ...msg] = message.split('-').map(value => value.trim());
    const { bgcolor, textcolor } = this.getColors(className);

    if (rest.length) {
      console.info(`%c${className}`, `padding:5px;background-color:${bgcolor};color:${textcolor};font-weight:bold;`, '-', ...msg, rest); //eslint-disable-line
    } else {
      console.info(`%c${className}`, `padding:5px;background-color:${bgcolor};color:${textcolor};font-weight:bold;`, '-', ...msg); //eslint-disable-line
    }
  }

  /**
   * Put a information level message into the developer console, will only show up in debug or info level
   * @param  {String} message the first variable passed to the console.log, generally a message
   *  formatted as `ClassName - propertyFunction() - optional additional message`
   * @param  {Object} rest an array of the rest of the variables passed to the console.log, typically class
   *  instances or variables in the file used for debugging
   */
  info(message, ...rest) {
    if (this.level === 'debug' || this.level === 'info') {
      if (window.console) {
        this.consoleLog(message, rest);
      }
    }
  }

  /**
   * Put a debug level message into the developer console, will only show up in debug level
   * @param  {String} message the first variable passed to the console.log, generally a message
   *  formatted as `ClassName - propertyFunction() - optional additional message`
   * @param  {Object} rest an array of the rest of the variables passed to the console.log, typically class
   *  instances or variables in the file used for debugging
   */
  debug(message, ...rest) {
    if (this.level === 'debug') {
      if (window.console) {
        this.consoleLog(message, rest);
      }
    }
  }

  /**
   * Put an error level message into the developer console, will only show up if there is some level specified
   * @param  {String} message the first variable passed to the console.log, generally a message
   *  formatted as `ClassName - propertyFunction() - optional additional message`
   * @param  {Object} stacktrace the error stack trace
   * @param  {Object} rest an array of the rest of the variables passed to the console.log, typically class
   *  instances or variables in the file used for debugging
   */
  error(message, stacktrace, ...rest) {
    if (window.console && this.level) {
      console.error(message, rest); // eslint-disable-line
      if (stacktrace !== undefined && stacktrace === true) {
        const trace = printStackTrace();
        console.error(trace.join('\n\n')); //eslint-disable-line
        console.error('-----------------------------'); //eslint-disable-line
      }
    }
  }
}

