    log.debug('Messages - handleBulkData()', data);

    _.each(data, (message) => {
      this.handleData(message);
    });
  }

  handleUTF8(message) {
    log.debug('Messages - handleUTF8()', message);

    this.app.toggleLogin(false);

    switch (message) {
      case 'updated':
        this.app.sendError(null, 'The client has been updated!');
        break;

      case 'full':
        this.app.sendError(null, 'The servers are currently full!');
        break;

      case 'error':
        this.app.sendError(null, 'The server has responded with an error!');
        break;

      case 'development':
        this.app.sendError(
