import Modules from '../../js/util/modules';
import Utils from '../../js/util/utils';

export default class OgreLord extends Combat {
  constructor(character) {
    super(character);
    this.character = character;

    this.dialogues = [
      'Get outta my swamp',
      'No, not the onion.',
      'My minions give me strength! You stand no chance!',
    ];

    this.minions = [];

    this.lastSpawn = 0;

    this.loaded = false;

    character.projectile = Modules.Projectiles.Boulder; // eslint-disable-line
    character.projectileName = 'projectile-boulder'; // eslint-disable-line

    character.onDeath(() => {
      this.reset();
    });
  }

  load() {
    this.talkingInterval = setInterval(() => {
      if (this.character.hasTarget()) {
        this.forceTalk(this.getMessage());
      }
    }, 9000);

    this.updateInterval = setInterval(() => {
      this.character.armourLevel = 50 + this.minions.length * 15;
    }, 2000);

    this.loaded = true;
  }

  hit(character, target, hitInfo) {
    if (this.isAttacked()) {
      this.beginMinionAttack();
    }

    if (!character.isNonDiagonal(target)) {
      const distance = character.getDistance(target);

      if (distance < 7) {
        hitInfo.isRanged = true; // eslint-disable-line
        character.attackRange = 7; // eslint-disable-line
      }
    }

    if (this.canSpawn()) {
      this.spawnMinions();
    }

    super.hit(character, target, hitInfo);
  }

  forceTalk(message) {
    if (!this.world) {
      return;
    }

    this.world.pushToAdjacentGroups(
      this.character.target.group,
      new Messages.NPC(Packets.NPCOpcode.Talk, {
        id: this.character.instance,
        text: message,
        nonNPC: true,
      }),
    );
  }

  getMessage() {
    return this.dialogues[Utils.randomInt(0, this.dialogues.length - 1)];
  }

  spawnMinions() {
    const
      xs = [414, 430, 415, 420, 429];


    const ys = [172, 173, 183, 185, 180];

    this.lastSpawn = new Date().getTime();

    this.forceTalk('Now you shall see my true power!');

    for (let i = 0; i < xs.length; i += 1) this.minions.push(this.world.spawnMob(12, xs[i], ys[i]));

    _.each(this.minions, (minion) => {
      minion.onDeath(() => {
        if (this.isLast()) {
          this.lastSpawn = new Date().getTime();
        }

        this.minions.splice(this.minions.indexOf(minion), 1);
      });

      if (this.isAttacked()) {
        this.beginMinionAttack();
      }
    });

    if (!this.loaded) {
      this.load();
    }
  }

  beginMinionAttack() {
    if (!this.hasMinions()) {
      return;
    }

    _.each(this.minions, (minion) => {
      const randomTarget = this.getRandomTarget();

      if (!minion.hasTarget() && randomTarget) {
        minion.combat.begin(randomTarget);
      }
    });
  }

  reset() {
    this.lastSpawn = 0;

    const listCopy = this.minions.slice();

    for (let i = 0; i < listCopy.length; i += 1) {
      this.world.kill(listCopy[i]);
    }

    clearInterval(this.talkingInterval);
    clearInterval(this.updateInterval);

    this.talkingInterval = null;
    this.updateInterval = null;

    this.loaded = false;
  }

  getRandomTarget() {
    if (this.isAttacked()) {
      const keys = Object.keys(this.attackers);
      const randomAttacker = this.attackers[keys[Utils.randomInt(0, keys.length)]];

      if (randomAttacker) {
        return randomAttacker;
      }
    }

    if (this.character.hasTarget()) {
      return this.character.target;
    }

    return null;
  }

  hasMinions() {
    return this.minions.length > 0;
  }

  isLast() {
    return this.minions.length === 1;
  }

  canSpawn() {
    return (
      new Date().getTime() - this.lastSpawn > 50000
      && !this.hasMinions()
      && this.isAttacked()
    );
  }
}

import Combat from '../../js/game/entity/character/combat/combat';
import Utils from '../../js/util/utils';
import Messages from '../../js/network/messages';

export default class PirateCaptain extends Combat {
  constructor(character) {
    super(character);
    this.character = Object.assign(character, {
      spawnDistance: 20,
    });

    this.teleportLocations = [];

    this.lastTeleportIndex = 0;
    this.lastTeleport = 0;

    this.location = {
      x: this.character.x,
      y: this.character.y,
    };

    this.load();
  }

  load() {
    const
      south = {
        x: 251,
        y: 574,
      };
    const west = {
      x: 243,
      y: 569,
    };
    const east = {
      x: 258,
      y: 568,
    };
    const north = {
      x: 251,
      y: 563,
    };

    this.teleportLocations.push(north, south, west, east);
  }

  hit(character, target, hitInfo) {
    if (this.canTeleport()) {
      this.teleport();
    } else {
      super.hit(character, target, hitInfo);
    }
  }

  teleport() {
    const position = this.getRandomPosition();

    if (!position) {
      return;
    }

    this.stop();

    this.lastTeleport = new Date().getTime();
    this.lastTeleportIndex = position.index;

    this.character.setPosition(position.x, position.y);

    if (this.world) {
      this.world.pushToGroup(
        this.character.group,
        new Messages.Teleport(
          this.character.instance,
          this.character.x,
          this.character.y,
          true,
        ),
      );
    }

    this.forEachAttacker((attacker) => {
      attacker.removeTarget();
    });

    if (this.character.hasTarget()) {
      this.begin(this.character.target);
    }
  }

  getRandomPosition() {
    const random = Utils.randomInt(0, this.teleportLocations.length - 1);
    const position = this.teleportLocations[random];

    if (!position || random === this.lastTeleportIndex) {
      return null;
    }

    return {
      x: position.x,
      y: position.y,
      index: random,
    };
  }

  canTeleport() {
    // Just randomize the teleportation for shits and giggles.
    return (
      new Date().getTime() - this.lastTeleport > 10000
      && Utils.randomInt(0, 4) === 2
    );
  }

  getHealthPercentage() {
    // Floor it to avoid random floats
    return Math.floor(
      (this.character.hitPoints / this.character.maxHitPoints) * 100,
    );
  }
}

import _ from 'underscore';
import Combat from '../../js/game/entity/character/combat/combat';
import Packets from '../../js/network/packets';
import Messages from '../../js/network/messages';
import Utils from '../../js/util/utils';

/**
 * This is where bosses start to get a bit more complex.
 * The queen ant will do an AoE attack after staggering for five seconds,
 * indicating to the players. If players are caught up in this, the terror
 * explosion sprite is drawn above them.
 */
export default class QueenAnt extends Combat {
  constructor(character) {
    super(character);

    this.lastActionThreshold = 10000; // Due to the nature of the AoE attack
    this.character = Object.assign(character, {
      spawnDistance: 18,
    });
    this.aoeTimeout = null;
    this.aoeCountdown = 5;
    this.aoeRadius = 2;
    this.lastAoE = 0;
    this.minionCount = 7;
    this.lastSpawn = 0;
    this.minions = [];
    this.frozen = false;

    /**
     * This is to prevent the boss from dealing
     * any powerful AoE attack after dying.
     */
    this.character.onDeath(() => {
      this.lastSpawn = 0;

      if (this.aoeTimeout) {
        clearTimeout(this.aoeTimeout);
        this.aoeTimeout = null;
      }

      const listCopy = this.minions.slice();

      for (let i = 0; i < listCopy.length; i += 1) {
        this.world.kill(listCopy[i]);
      }
    });

    this.character.onReturn(() => {
      clearTimeout(this.aoeTimeout);
      this.aoeTimeout = null;
    });
  }

  begin(attacker) {
    this.resetAoE();
    super.begin(attacker);
  }

  hit(attacker, target, hitInfo) {
    if (this.frozen) {
      return;
    }

    if (this.canCastAoE()) {
      this.doAoE();
      return;
    }

    if (this.canSpawn()) {
      this.spawnMinions();
    }

    if (this.isAttacked()) {
      this.beginMinionAttack();
    }

    super.hit(attacker, target, hitInfo);
  }

  /**
   * The reason this function does not use its superclass
   * representation is because of the setTimeout function
   * which does not allow us to call super().
   */
  doAoE() {
    this.resetAoE();
    this.lastHit = this.getTime();
    this.pushFreeze(true);
    this.pushCountdown(this.aoeCountdown);

    this.aoeTimeout = setTimeout(() => {
      this.dealAoE(this.aoeRadius, true);
      this.pushFreeze(false);
    }, 5000);
  }

  spawnMinions() {
    this.lastSpawn = new Date().getTime();

    for (let i = 0; i < this.minionCount; i += 1) {
      this.minions.push(
        this.world.spawnMob(13, this.character.x, this.character.y),
      );
    }

    _.each(this.minions, (minion) => {
      minion.aggressive = true;
      minion.spawnDistance = 12;

      minion.onDeath(() => {
        if (this.isLast()) {
          this.lastSpawn = new Date().getTime();
        }

        this.minions.splice(this.minions.indexOf(minion), 1);
      });

      if (this.isAttacked()) {
        this.beginMinionAttack();
      }
    });
  }

  beginMinionAttack() {
    if (!this.hasMinions()) {
      return;
    }

    _.each(this.minions, (minion) => {
      const randomTarget = this.getRandomTarget();

      if (!minion.hasTarget() && randomTarget) {
        minion.combat.begin(randomTarget);
      }
    });
  }

  resetAoE() {
    this.lastAoE = new Date().getTime();
  }

  getRandomTarget() {
    if (this.isAttacked()) {
      const keys = Object.keys(this.attackers);
      const randomAttacker = this.attackers[keys[Utils.randomInt(0, keys.length)]];

      if (randomAttacker) {
        return randomAttacker;
      }
    }

    if (this.character.hasTarget()) {
      return this.character.target;
    }

    return null;
  }

  pushFreeze(state) {
    this.character.frozen = state;
    this.character.stunned = state;
  }

  pushCountdown(count) {
    this.world.pushToAdjacentGroups(
      this.character.group,
      new Messages.NPC(Packets.NPCOpcode.Countdown, {
        id: this.character.instance,
        countdown: count,
      }),
    );
  }

  getMinions() {
    this.world.getGrids();
  }

  isLast() {
    return this.minions.length === 1;
  }

  hasMinions() {
    return this.minions.length > 0;
  }

  canCastAoE() {
    return new Date().getTime() - this.lastAoE > 30000;
  }

  canSpawn() {
    return (
      new Date().getTime() - this.lastSpawn > 45000
      && !this.hasMinions()
      && this.isAttacked()
    );
  }
}

import _ from 'underscore';
import Combat from '../../js/game/entity/character/combat/combat';
import Utils from '../../js/util/utils';

/**
 * First of its kind, the Skeleton King will spawn 4 minions.
 * Two sorcerers on (x + 1, y + 1) & (x - 1, y + 1)
 *
 * And two death knights on (x + 1, y - 1) & (x - 1, y - 1)
 */
export default class SkeletonKing extends Combat {
  constructor(character) {
    super(character);

    this.character = Object.assign(character, {
      spawnDistance: 10,
    });
    this.lastSpawn = 0;
    this.minions = [];

    character.onDeath(() => {
      this.reset();
    });
  }

  reset() {
    this.lastSpawn = 0;

    const listCopy = this.minions.slice();

    for (let i = 0; i < listCopy.length; i += 1) {
      this.world.kill(listCopy[i]);
    }
  }

  hit(character, target, hitInfo) {
    if (this.isAttacked()) {
      this.beginMinionAttack();
    }

    if (this.canSpawn()) {
      this.spawnMinions();
    }

    super.hit(character, target, hitInfo);
  }

  spawnMinions() {
    const x = this.character.x;
    const y = this.character.y;

    this.lastSpawn = new Date().getTime();

    if (!this.colliding(x + 2, y - 2)) {
      this.minions.push(this.world.spawnMob(17, x + 2, y + 2));
    }

    if (!this.colliding(x - 2, y - 2)) {
      this.minions.push(this.world.spawnMob(17, x - 2, y + 2));
    }

    if (!this.colliding(x + 1, y + 1)) {
      this.minions.push(this.world.spawnMob(11, x + 1, y - 1));
    }

    if (!this.colliding(x - 1, y + 1)) {
      this.minions.push(this.world.spawnMob(11, x - 1, y - 1));
    }

    _.each(this.minions, (minion) => {
      minion.onDeath(() => {
        if (this.isLast()) {
          this.lastSpawn = new Date().getTime();
        }

        this.minions.splice(this.minions.indexOf(minion), 1);
      });

      if (this.isAttacked()) {
        this.beginMinionAttack();
      }
    });
  }

  beginMinionAttack() {
    if (!this.hasMinions()) {
      return;
    }

    _.each(this.minions, (minion) => {
      const randomTarget = this.getRandomTarget();

      if (!minion.hasTarget() && randomTarget) {
        minion.combat.begin(randomTarget);
      }
    });
  }

  getRandomTarget() {
    if (this.isAttacked()) {
      const keys = Object.keys(this.attackers);
      const randomAttacker = this.attackers[keys[Utils.randomInt(0, keys.length)]];

      if (randomAttacker) {
        return randomAttacker;
      }
    }

    if (this.character.hasTarget()) {
      return this.character.target;
    }

    return null;
  }

  hasMinions() {
    return this.minions.length > 0;
  }

  isLast() {
    return this.minions.length === 1;
  }

  canSpawn() {
    return (
      new Date().getTime() - this.lastSpawn > 25000
      && !this.hasMinions()
      && this.isAttacked()
    );
  }
}

import _ from 'underscore';
import Combat from '../../js/game/entity/character/combat/combat';
import Messages from '../../js/network/messages';
import Packets from '../../js/network/packets';
import Utils from '../../js/util/utils';

export default class Tenebris extends Combat {
  constructor(character) {
    super(character);

    this.character = Object.assign(character, {
      spawnDistance: 24,
    });
    this.illusions = [];
    this.firstIllusionKilled = false;
    this.lastIllusion = new Date().getTime();
    this.respawnDelay = 95000;

    character.onDeath(() => {
      if (this.isIllusion()) {
        if (!this.firstIllusionKilled) {
          this.spawnTenbris();
        } else {
          this.removeIllusions();
          this.reset();
        }
      }
    });

    if (!this.isIllusion()) {
      this.forceTalk('Who dares summon Tenebris!');
    }
  }

  reset() {
    this.illusions = [];
    this.firstIllusionKilled = false;

    setTimeout(() => {
      const offset = Utils.positionOffset(4);

      this.world.spawnMob(105, 48 + offset.x, 338 + offset.y);
    }, this.respawnDelay);
  }

  hit(attacker, target, hitInfo) {
    if (this.isAttacked()) {
      this.beginIllusionAttack();
    }

    if (this.canSpawn()) {
      this.spawnIllusions();
    }

    super.hit(attacker, target, hitInfo);
  }

  spawnTenbris() {
    this.world.spawnMob(104, this.character.x, this.character.y);
  }

  spawnIllusions() {
    this.illusions.push(
      this.world.spawnMob(105, this.character.x + 1, this.character.y + 1),
    );
    this.illusions.push(
      this.world.spawnMob(105, this.character.x - 1, this.character.y + 1),
    );

    _.each(this.illusions, (illusion) => {
      illusion.onDeath(() => {
        if (this.isLast()) {
          this.lastIllusion = new Date().getTime();
        }

        this.illusions.splice(this.illusions.indexOf(illusion), 1);
      });

      if (this.isAttacked()) {
        this.beginIllusionAttack();
      }
    });

    this.character.setPosition(62, 343);
    this.world.pushToGroup(
      this.character.group,
      new Messages.Teleport(
        this.character.instance,
        this.character.x,
        this.character.y,
        true,
      ),
    );
  }

  removeIllusions() {
    this.lastIllusion = 0;

    const listCopy = this.illusions.slice();

    for (let i = 0; i < listCopy.length; i += 1) {
      this.world.kill(listCopy[i]);
    }
  }

  beginIllusionAttack() {
    if (!this.hasIllusions()) {
      return;
    }

    _.each(this.illusions, (illusion) => {
      const target = this.getRandomTarget();

      if (!illusion.hasTarget && target) {
        illusion.combat.begin(target);
      }
    });
  }

  getRandomTarget() {
    if (this.isAttacked()) {
      const keys = Object.keys(this.attackers);
      const randomAttacker = this.attackers[keys[Utils.randomInt(0, keys.length)]];

      if (randomAttacker) {
        return randomAttacker;
      }
    }

    if (this.character.hasTarget()) {
      return this.character.target;
    }

    return null;
  }

  forceTalk(instance, message) {
    if (!this.world) {
      return;
    }

    this.world.pushToAdjacentGroups(
      this.character.target.group,
      new Messages.NPC(Packets.NPCOpcode.Talk, {
        id: instance,
        text: message,
        nonNPC: true,
      }),
    );
  }

  isLast() {
    return this.illusions.length === 1;
  }

  canSpawn() {
    return (
      !this.isIllusion()
      && !this.hasIllusions
      && new Date().getTime() - this.lastIllusion === 45000
      && Utils.randomInt(0, 4) === 2
    );
  }

  isIllusion() {
    return this.character.id === 105;
  }

  hasIllusions() {
    return this.illusions.length > 0;
  }
}

import Item from '../../js/game/entity/objects/item.js';
