import log from '../util/log';

export default class Creator {
  constructor(mysql) {
    this.mysql = mysql;
  }

  tableNotExists(tableName, ifNotExists) {
    let exists = 0;

    this.mysql.connection.query(
      'SELECT count(*) as count FROM information_schema.TABLES WHERE (TABLE_SCHEMA = ?) AND (TABLE_NAME = ?)',
      [this.mysql.database, tableName],
      (err, rows) => {
        if (err) {
          log.error(err);
          throw err;
        }

        exists = rows[0].count;

        if (exists === 0) {
          ifNotExists();
        }
      },
    );
  }

  createTables() {
    function handleError(tableName) {
      return (error) => {
        if (error) {
          log.error(
            `[MySQL] Failed to created table ${tableName} : ${error}`,
          );
          throw error;
        } else log.notice(`[MySQL] Created table ${tableName}`);
      };
    }

    this.tableNotExists('player_data', () => {
      this.mysql.connection.query(
        'CREATE TABLE player_data ('
        + 'username varchar(64),'
        + 'password varchar(64),'
        + 'email varchar(64),'
        + 'x int,'
        + 'y int,'
        + 'experience int,'
        + 'kind int,'
        + 'rights int,'
        + 'poisoned tinyint,'
        + 'hitPoints int,'
        + 'mana int,'
        + 'pvpKills int,'
        + 'pvpDeaths int,'
        + 'rank int,'
        + 'ban int(64),'
        + 'mute varchar(64),'
        + 'membership varchar(64),'
        + 'lastLogin varchar(64),'
        + 'guild varchar(64),'
        + 'lastWarp varchar(64),'
        + 'PRIMARY KEY(username))',
        handleError('player_data'),
      );
    });

    this.tableNotExists('player_equipment', () => {
      this.mysql.connection.query(
        'CREATE TABLE player_equipment ('
        + 'username varchar(64),'
        + 'armour varchar(64),'
        + 'weapon varchar(64),'
        + 'pendant varchar(64),'
        + 'ring varchar(64),'
        + 'boots varchar(64),'
        + 'PRIMARY KEY(username))',
        handleError('player_equipment'),
      );
    });

    this.tableNotExists('player_quests', () => {
      this.mysql.connection.query(
        'CREATE TABLE player_quests ('
        + 'username varchar(64),'
        + 'ids text COLLATE utf8_unicode_ci NOT NULL,'
        + 'stages text COLLATE utf8_unicode_ci NOT NULL,'
        + 'PRIMARY KEY(username))',
        handleError('player_quests'),
      );
    });

    this.tableNotExists('player_achievements', () => {
      this.mysql.connection.query(
        'CREATE TABLE player_achievements ('
        + 'username varchar(64),'
        + 'ids text COLLATE utf8_unicode_ci NOT NULL,'
        + 'progress text COLLATE utf8_unicode_ci NOT NULL,'
        + 'PRIMARY KEY(username))',
        handleError('player_achievements'),
      );
    });

    this.tableNotExists('player_bank', () => {
      this.mysql.connection.query(
        'CREATE TABLE player_bank ('
        + 'username varchar(64),'
        + 'ids text COLLATE utf8_unicode_ci NOT NULL,'
        + 'counts text COLLATE utf8_unicode_ci NOT NULL,'
        + 'abilities text COLLATE utf8_unicode_ci NOT NULL,'
        + 'abilityLevels text COLLATE utf8_unicode_ci NOT NULL,'
        + 'PRIMARY KEY(username))',
        handleError('player_bank'),
      );
    });

    this.tableNotExists('player_abilities', () => {
      this.mysql.connection.query(
        'CREATE TABLE player_abilities ('
        + 'username varchar(64),'
        + 'abilities text COLLATE utf8_unicode_ci NOT NULL,'
        + 'abilityLevels text COLLATE utf8_unicode_ci NOT NULL,'
        + 'shortcuts text COLLATE utf8_unicode_ci NOT NULL,'
        + 'PRIMARY KEY (username))',
        handleError('player_abilities'),
      );
    });

    this.tableNotExists('player_inventory', () => {
      this.mysql.connection.query(
        'CREATE TABLE player_inventory ('
        + 'username varchar(64),'
        + 'ids text COLLATE utf8_unicode_ci NOT NULL,'
        + 'counts text COLLATE utf8_unicode_ci NOT NULL,'
        + 'abilities text COLLATE utf8_unicode_ci NOT NULL,'
        + 'abilityLevels text COLLATE utf8_unicode_ci NOT NULL,'
        + 'PRIMARY KEY(username))',
        handleError('player_inventory'),
      );
    });

    this.tableNotExists('ipbans', () => {
      this.mysql.connection.query(
        'CREATE TABLE IF NOT EXISTS ipbans ('
        + 'ip varchar(64),'
        + 'ipban int(64),'
        + 'PRIMARY KEY(ip))',
        handleError('ipbans'),
      );
    });
  }

  save(player) {
    const queryKey = player.isNew ? 'INSERT INTO' : 'UPDATE IGNORE';
    const playerData = this.formatData(this.getPlayerData(player), 'data');
    const equipmentData = this.formatData(this.getPlayerData(player), 'equipment');

    const handleError = (error) => {
      if (error) log.error(error);
    };

    this.mysql.connection.query(
      `${queryKey} \`player_data\` SET ?`,
      playerData,
      handleError,
    );
    this.mysql.connection.query(
      `${queryKey} \`player_equipment\` SET ?`,
      equipmentData,
      handleError,
    );
    this.mysql.connection.query(
      `${queryKey} \`player_inventory\` SET ?`,
      player.inventory.getArray(),
      handleError,
    );
    this.mysql.connection.query(
      `${queryKey} \`player_abilities\` SET ?`,
      player.abilities.getArray(),
      handleError,
    );
    this.mysql.connection.query(
      `${queryKey} \`player_bank\` SET ?`,
      player.bank.getArray(),
      handleError,
    );
    this.mysql.connection.query(
      `${queryKey} \`player_quests\` SET ?`,
      player.quests.getQuests(),
      handleError,
    );
    this.mysql.connection.query(
      `${queryKey} \`player_achievements\` SET ?`,
      player.quests.getAchievements(),
      handleError,
    );
  }

  formatData(data, type) {
    let formattedData;

    switch (type) {
      default:
        break;
      case 'data':
        formattedData = {
          username: data.username,
          email: data.email,
          x: data.x,
          y: data.y,
          experience: data.experience,
          kind: data.kind,
          rights: data.rights,
          poisoned: data.poisoned,
          hitPoints: data.hitPoints.getHitPoints(),
          mana: data.mana.getMana(),
          pvpKills: data.pvpKills,
          pvpDeaths: data.pvpDeaths,
          rank: data.rank,
          ban: data.ban,
          mute: data.mute,
          membership: data.membership,
          lastLogin: data.lastLogin,
          guild: data.guild,
          lastWarp: data.lastWarp,
        };
        break;

      case 'equipment':
        formattedData = {
          username: data.username,
          armour: data.armour.toString(),
          weapon: data.weapon.toString(),
          pendant: data.pendant.toString(),
          ring: data.ring.toString(),
          boots: data.boots.toString(),
        };

        break;
    }

    return formattedData;
  }

  getPlayerData(player) {
    return {
      username: player.username,
      email: player.email ? player.email : 'null',
      x: player.x ? player.x : -1,
      y: player.y ? player.y : -1,
      kind: player.kind ? player.kind : 0,
      rights: player.rights ? player.rights : 0,
      hitPoints: player.hitPoints ? player.hitPoints : 100,
      mana: player.mana ? player.mana : 20,
      poisoned: player.poisoned ? player.poisoned : 0,
      experience: player.experience ? player.experience : 0,
      ban: player.ban ? player.ban : 0,
      mute: player.mute ? player.mute : 0,
      rank: player.rank ? player.rank : 0,
      membership: player.membership ? player.membership : 0,
      lastLogin: player.lastLogin ? player.lastLogin : 0,
      guild: player.guild ? player.guild : '',
      pvpKills: player.pvpKills ? player.pvpKills : 0,
      pvpDeaths: player.pvpDeaths ? player.pvpDeaths : 0,
      lastWarp: player.warp.lastWarp ? player.warp.lastWarp : 0,
      armour: [
        player.armour ? player.armour.getId() : 114,
        player.armour ? player.armour.getCount() : 0,
        player.armour ? player.armour.getAbility() : 0,
        player.armour ? player.armour.getAbilityLevel() : 0,
      ],
      weapon: [
        player.weapon ? player.weapon.getId() : -1,
        player.weapon ? player.weapon.getCount() : 0,
        player.weapon ? player.weapon.getAbility() : 0,
        player.weapon ? player.weapon.getAbilityLevel() : 0,
      ],
      pendant: [
        player.pendant ? player.pendant.getId() : -1,
        player.pendant ? player.pendant.getCount() : 0,
        player.pendant ? player.pendant.getAbility() : 0,
        player.pendant ? player.pendant.getAbilityLevel() : 0,
      ],
      ring: [
        player.ring ? player.ring.getId() : -1,
        player.ring ? player.ring.getCount() : 0,
        player.ring ? player.ring.getAbility() : 0,
        player.ring ? player.ring.getAbilityLevel() : 0,
      ],
      boots: [
        player.boots ? player.boots.getId() : -1,
        player.boots ? player.boots.getCount() : 0,
        player.boots ? player.boots.getAbility() : 0,
        player.boots ? player.boots.getAbilityLevel() : 0,
      ],
    };
  }
}

import log from '../util/log';

export default class Loader {
  constructor(mysql) {
    this.mysql = mysql;
  }

  getInventory(player, callback) {
    this.mysql.connection.query(
      'SELECT * FROM `player_inventory` WHERE `player_inventory`.`username`=?',
      [player.username],
      (error, rows) => {
        const info = rows.shift();

        if (info.username !== player.username) {
          log.notice(
            `Mismatch whilst retrieving inventory data for: ${player.username}`,
          );
        }

        callback(
          info.ids.split(' '),
          info.counts.split(' '),
          info.abilities.split(' '),
          info.abilityLevels.split(' '),
        );
      },
    );
  }

  getBank(player, callback) {
    this.mysql.connection.query(
      'SELECT * FROM `player_bank` WHERE `player_bank`.`username`=?',
      [player.username],
      (error, rows) => {
        const info = rows.shift();

        if (info.username !== player.username) {
          log.notice(
            `Mismatch whilst retrieving bank data for: ${player.username}`,
          );
        }

        callback(
          info.ids.split(' '),
          info.counts.split(' '),
          info.abilities.split(' '),
          info.abilityLevels.split(' '),
        );
      },
    );
  }

  getQuests(player, callback) {
    this.mysql.connection.query(
      'SELECT * FROM `player_quests` WHERE `player_quests`.`username`=?',
      [player.username],
      (error, rows) => {
        const info = rows.shift();

        if (info.username !== player.username) {
          log.notice(
            `Mismatch whilst retrieving quest data for: ${player.username}`,
          );
        }

        callback(info.ids.split(' '), info.stages.split(' '));
      },
    );
  }

  getAchievements(player, callback) {
    this.mysql.connection.query(
      'SELECT * FROM `player_achievements` WHERE `player_achievements`.`username`=?',
      [player.username],
      (error, rows) => {
        const info = rows.shift();

        if (info.username !== player.username) {
          log.notice(
            `Mismatch whilst retreiving achievement data for: ${
              player.username}`,
          );
        }

        callback(info.ids.split(' '), info.progress.split(' '));
      },
    );
  }
}

import mysql from 'mysql';
import _ from 'underscore';
import log from '../util/log';
import Creator from './creator';
import Loader from './loader';
import Config from '../../config.json';

export default class MySQL {
  constructor(host, port, user, pass, database) {
    /**
     * Main file for MySQL, it splits into Creator and Loader.
     * Responsible for creating and loading data, respectively.
     */
    this.host = host;
    this.port = port;
    this.user = user;
    this.password = pass;
    this.database = database;

    this.loader = null;

    this.connect(
      true,
      false,
    );

    this.loadCreator();
    this.loadCallbacks();
  }

  connect(usingDB, forceCallbacks) {
    if (this.connection) {
      this.connection.destroy();
      this.connection = null;
    }

    this.connection = mysql.createConnection({
      host: this.host,
      port: this.port,
      user: this.user,
      password: this.password,
      database: usingDB ? this.database : null,
    });

    if (forceCallbacks) this.loadCallbacks();
  }

  loadCallbacks() {
    this.connection.connect((error) => {
      if (error) {
        log.notice('[MySQL] No database found...');
        log.notice(error);
        this.connect(
          false,
          false,
        );
        this.loadDatabases();
        return;
      }

      this.creator.createTables();
      log.notice('Successfully established connection to the MySQL database!');
      this.loader = new Loader(this);
    });

    this.connection.on('error', (error) => {
      log.error('MySQL database disconnected.');
      log.error(error);

      this.connect(
        true,
        true,
      );
    });

    this.onSelected(() => {
      this.creator.createTables();
    });
  }

  loadCreator() {
    if (this.creator) return;

    this.creator = new Creator(this);
  }

  login(player) {
    let
      found;

    log.notice(`Initiating login for: ${player.username}`);

    this.connection.query(
      'SELECT * FROM `player_data`, `player_equipment` WHERE `player_data`.`username`= ? AND `player_data`.`password`= ?',
      [player.username, player.password],
      (error, rows) => {
        if (error) {
          log.error(error);
          throw error;
        }

        _.each(rows, (row) => {
          if (row.username === player.username) {
            found = true;

            const data = row;

            data.armour = data.armour.split(',').map(Number);
            data.weapon = data.weapon.split(',').map(Number);
            data.pendant = data.pendant.split(',').map(Number);
            data.ring = data.ring.split(',').map(Number);
            data.boots = data.boots.split(',').map(Number);

            player.load(data);
            player.intro();
          }
        });

        if (player.isGuest) {
          // register the guest account
          this.register(player);
        } else if (!found) {
          log.notice(`Mysql.login(player) failed for ${player.username}`);
          player.invalidLogin();
        }
      },
    );
  }

  register(player) {
    this.connection.query(
      'SELECT * FROM `player_data` WHERE `player_data`.`username`= ?',
      [player.username],
      (error, rows) => {
        let exists;

        if (error) {
          log.error(error);
          throw error;
        }

        _.each(rows, (row) => {
          if (row.name === player.username) exists = true;
        });

        if (!exists) {
          log.notice(`No player data found for: ${player.username}`);

          player.isNew = true; // eslint-disable-line
          player.load(this.creator.getPlayerData(player));

          this.creator.save(player);

          player.isNew = false; // eslint-disable-line
          player.intro();
        } else {
          log.notice('MySQL.register(player) Error: Username already exists.');
          player.notify('This username is already taken!');
        }
      },
    );
  }

  delete(player) {
    const
      tables = [
        'player_data',
        'player_equipment',
        'player_inventory',
        'player_abilities',
        'player_bank',
        'player_quests',
        'player_achievements',
      ];

    _.each(tables, (table) => {
      this.connection.query(
        `DELETE FROM \`${table}\` WHERE \`${table}\`.\`username\`=?`,
        [player.username],
        (error) => {
          if (error) {
            log.error(`Error while deleting user: ${player.username}`);
          }
        },
      );
    });
  }

  loadDatabases() {
    log.notice('[MySQL] Creating database....');

    this.connection.query(
      `CREATE DATABASE IF NOT EXISTS ${Config.mysqlDatabase}`,
      (error) => {
        if (error) {
          throw error;
        }

        log.notice('[MySQL] Successfully created database.');

        this.connection.query(`USE ${Config.mysqlDatabase}`, () => {
          if (this.selectDatabase_callback) {
            this.selectDatabase_callback();
          }
        });
      },
    );
  }

  queryData(type, database, data) {
    this.connection.query(`${type} ${database} SET ?`, data, (
      error,
    ) => {
      if (error) {
        throw error;
      }

      log.notice(`Successfully updated ${database}`);
    });
  }

  alter(database, column, type) {
    this.connection.query(
      `ALTER TABLE ${database} ADD ${column} ${type}`,
      (error) => {
        if (error) {
          log.error('Malformation in the database type and/or type.');
          return;
        }

        log.notice(`Database ${database} has been successfully altered.`);
      },
    );
  }

  onSelected(callback) {
    this.selectDatabase_callback = callback;
  }
}

import Entity from '../entity';
import Combat from './combat/combat';
import Modules from '../../../util/modules';
import MobsDictionary from '../../../util/mobs';

export default class Character extends Entity {
  constructor(id, type, instance, x, y) {
    super(id, type, instance, x, y);

    this.level = -1;
    this.loaded = false;

    this.movementSpeed = 150;
    this.attackRange = 1;
    this.attackRate = 1000;
    this.healingRate = 7000;

    this.spawnDistance = 7;

    this.previousX = -1;
    this.previousY = -1;

    this.hitPoints = -1;
    this.maxHitPoints = -1;

    this.dead = false;
    this.aggressive = false;
    this.aggroRange = 2;

    this.frozen = false;
    this.stunned = false;

    this.target = null;
    this.potentialTarget = null;

    this.stunTimeout = null;

    this.projectile = Modules.Projectiles.Arrow;
    this.projectileName = 'projectile-pinearrow';

    this.healingInterval = null;

    this.mobsDictionary = MobsDictionary;

    this.loadCombat();
    this.startHealing();
  }

  loadCombat() {
    if (this.mobsDictionary.hasCombatPlugin(this.id)) {
      this.combat = new (this.mobsDictionary.isNewCombatPlugin(this.id))(this);
    } else {
      this.combat = new Combat(this);
    }
  }

  setStun(stun) {
    this.stunned = stun;

    if (this.stunCallback) {
      this.stunCallback(stun);
    }
  }

  startHealing() {
    this.healingInterval = setInterval(() => {
      if (
        !this.hasTarget()
        && !this.combat.isAttacked()
        && !this.dead
        && this.loaded
      ) {
        this.heal(1);
      }
    }, 5000);
  }

  stopHealing() {
    clearInterval(this.healingInterval);
    this.healingInterval = null;
  }

  hit(attacker) {
    if (this.hitCallback) {
      this.hitCallback(attacker);
    }
  }

  heal(amount) {
    this.setHitPoints(this.hitPoints + amount);

    if (this.hitPoints > this.maxHitPoints) {
      this.hitPoints = this.maxHitPoints;
    }
  }

  isRanged() {
    return this.attackRange > 1;
  }

  applyDamage(damage) {
    this.hitPoints -= damage;
  }

  isDead() {
    return this.hitPoints < 1 || this.dead;
  }

  getCombat() {
    return this.combat;
  }

  getHitPoints() {
    return this.hitPoints;
  }

  getMaxHitPoints() {
    return this.maxHitPoints;
  }

  setPosition(x, y) {
    super.setPosition(x, y);

    if (this.movementCallback) {
      this.movementCallback(x, y);
    }
  }

  setTarget(target) {
    this.target = target;

    if (this.targetCallback) {
      this.targetCallback(target);
    }
  }

  setPotentialTarget(potentialTarget) {
    this.potentialTarget = potentialTarget;
  }

  setHitPoints(hitPoints) {
    this.hitPoints = hitPoints;

    if (this.hitPointsCallback) {
      this.hitPointsCallback();
    }
  }

  getProjectile() {
    return this.projectile;
  }

  getProjectileName() {
    return this.projectileName;
  }

  getDrop() {
    return null;
  }

  hasMaxHitPoints() {
    return this.hitPoints >= this.maxHitPoints;
  }

  removeTarget() {
    if (this.removeTargetCallback) {
      this.removeTargetCallback();
    }

    this.target = null;
  }

  hasTarget() {
    return !(this.target === null);
  }

  hasPotentialTarget(potentialTarget) {
    return this.potentialTarget === potentialTarget;
  }

  clearTarget() {
    this.target = null;
  }

  onTarget(callback) {
    this.targetCallback = callback;
  }

  onRemoveTarget(callback) {
    this.removeTargetCallback = callback;
  }

  onMovement(callback) {
    this.movementCallback = callback;
  }

  onHit(callback) {
    this.hitCallback = callback;
  }

  onHealthChange(callback) {
    this.hitPointsCallback = callback;
  }

  onDamage(callback) {
    this.damageCallback = callback;
  }

  onStunned(callback) {
    this.stunCallback = callback;
  }

  onSubAoE(callback) {
    this.subAoECallback = callback;
  }
}

import _ from 'underscore';
import CombatQueue from './combatqueue';
import Utils from '../../../../util/utils';
import Formulas from '../../../formulas';
import Hit from './hit';
import Modules from '../../../../util/modules';
import Messages from '../../../../network/messages';
import Packets from '../../../../network/packets';

export default class Combat {
  constructor(character) {
    this.character = character;
    this.world = null;

    this.attackers = {};

    this.retaliate = false;

    this.queue = new CombatQueue();

    this.attacking = false;

    this.attackLoop = null;
    this.followLoop = null;
    this.checkLoop = null;

    this.first = false;
    this.started = false;
    this.lastAction = -1;
    this.lastHit = -1;

    this.lastActionThreshold = 7000;

    this.cleanTimeout = null;

    this.character.onSubAoE((radius, hasTerror) => {
      this.dealAoE(radius, hasTerror);
    });

    this.character.onDamage((target, hitInfo) => {
      if (
        this.isPlayer()
        && this.character.hasBreakableWeapon()
        && Formulas.getWeaponBreak(this.character, target)
      ) this.character.breakWeapon();

      if (hitInfo.type === Modules.Hits.Stun) {
        target.setStun(true);

        if (target.stunTimeout) clearTimeout(target.stunTimeout);

        target.stunTimeout = setTimeout(() => { // eslint-disable-line
          target.setStun(false);
        }, 3000);
      }
    });
  }

  begin(attacker) {
    this.start();

    this.character.setTarget(attacker);
    this.addAttacker(attacker);

    attacker.combat.addAttacker(this.character); // For mobs attacking players..

    this.attack(attacker);
  }

  start() {
    if (this.started) return;

    this.lastAction = new Date().getTime();

    this.attackLoop = setInterval(() => {
      this.parseAttack();
    }, this.character.attackRate);

    this.followLoop = setInterval(() => {
      this.parseFollow();
    }, 400);

    this.checkLoop = setInterval(() => {
      if (this.getTime() - this.lastAction > this.lastActionThreshold) {
        this.stop();

        this.forget();
      }
    }, 1000);

    this.started = true;
  }

  stop() {
    if (!this.started) return;

    clearInterval(this.attackLoop);
    clearInterval(this.followLoop);
    clearInterval(this.checkLoop);

    this.attackLoop = null;
    this.followLoop = null;
    this.checkLoop = null;

    this.started = false;
  }

  parseAttack() {
    if (!this.world || !this.queue || this.character.stunned) {
      return;
    }

    if (this.character.hasTarget() && this.inProximity()) {
      if (this.queue.hasQueue()) {
        this.hit(this.character, this.character.target, this.queue.getHit());
      }

      if (this.character.target && !this.character.target.isDead()) {
        this.attack(this.character.target);
      }

      this.lastAction = this.getTime();
    } else this.queue.clear();
  }

  parseFollow() {
    if (this.character.frozen || this.character.stunned) {
      return;
    }

    if (this.isMob()) {
      if (!this.character.isRanged()) {
        this.sendFollow();
      }

      if (this.isAttacked() || this.character.hasTarget()) {
        this.lastAction = this.getTime();
      }

      if (this.onSameTile()) {
        const newPosition = this.getNewPosition();

        this.move(this.character, newPosition.x, newPosition.y);
      }

      if (this.character.hasTarget() && !this.inProximity()) {
        const attacker = this.getClosestAttacker();

        if (attacker) {
          this.follow(this.character, attacker);
        }
      }
    }
  }

  attack(target) {
    let
      hit;

    if (this.isPlayer()) hit = this.character.getHit(target);
    else {
      hit = new Hit(
        Modules.Hits.Damage,
        Formulas.getDamage(this.character, target),
      );
    }

    if (!hit) return;

    this.queue.add(hit);
  }

  dealAoE(radius, hasTerror) {
    /**
     * TODO - Find a way to implement special effects without hardcoding them.
     */

    if (!this.world) return;

    const entities = this.world
      .getGrids()
      .getSurroundingEntities(this.character, radius);

    _.each(entities, (entity) => {
      const hitData = new Hit(
        Modules.Hits.Damage,
        Formulas.getAoEDamage(this.character, entity),
      ).getData();

      hitData.isAoE = true;
      hitData.hasTerror = hasTerror;

      this.hit(this.character, entity, hitData);
    });
  }

  forceAttack() {
    if (!this.character.target || !this.inProximity()) return;

    this.stop();
    this.start();

    this.attackCount(2, this.character.target);
    this.hit(this.character, this.character.target, this.queue.getHit());
  }

  attackCount(count, target) {
    for (let i = 0; i < count; i += 1) this.attack(target);
  }

  addAttacker(character) {
    if (this.hasAttacker(character)) return;

    this.attackers[character.instance] = character;
  }

  removeAttacker(character) {
    if (this.hasAttacker(character)) delete this.attackers[character.instance];

    if (!this.isAttacked()) this.sendToSpawn();
  }

  sendToSpawn() {
    if (!this.isMob()) return;

    this.character.return();

    this.world.pushBroadcast(
      new Messages.Movement(Packets.MovementOpcode.Move, [
        this.character.instance,
        this.character.x,
        this.character.y,
        false,
        false,
      ]),
    );
  }

  hasAttacker(character) {
    if (!this.isAttacked()) {
      return null;
    }

    return character.instance in this.attackers;
  }

  onSameTile() {
    if (!this.character.target || this.character.type !== 'mob') {
      return false;
    }

    return (
      this.character.x === this.character.target.x
      && this.character.y === this.character.target.y
    );
  }

  isAttacked() {
    return this.attackers && Object.keys(this.attackers).length > 0;
  }

  getNewPosition() {
    const
      position = {
        x: this.character.x,
        y: this.character.y,
      };

    const random = Utils.randomInt(0, 3);

    if (random === 0) position.x += 1;
    else if (random === 1) position.y -= 1;
    else if (random === 2) position.x -= 1;
    else if (random === 3) position.y += 1;

    return position;
  }

  isRetaliating() {
    return (
      this.isPlayer()
      && !this.character.hasTarget()
      && this.retaliate
      && !this.character.moving
      && new Date().getTime() - this.character.lastMovement > 1500
    );
  }

  inProximity() {
    if (!this.character.target) {
      return false;
    }

    const targetDistance = this.character.getDistance(this.character.target);
    const range = this.character.attackRange;

    if (this.character.isRanged()) {
      return targetDistance <= range;
    }

    return this.character.isNonDiagonal(this.character.target);
  }

  getClosestAttacker() {
    let closest = null;
    const lowestDistance = 100;

    this.forEachAttacker((attacker) => {
      const distance = this.character.getDistance(attacker);

      if (distance < lowestDistance) {
        closest = attacker;
      }
    });

    return closest;
  }

  setWorld(world) {
    if (!this.world) this.world = world;
  }

  forget() {
    this.attackers = {};
    this.character.removeTarget();

    if (this.forgetCallback) this.forgetCallback();
  }

  move(character, x, y) {
    /**
     * The server and mob types can parse the mob movement
     */

    if (character.type !== 'mob') return;

    character.move(x, y);
  }

  hit(character, target, hitInfo) {
    const
      time = this.getTime();

    if (time - this.lastHit < this.character.attackRate && !hitInfo.isAoE) return;

    if (character.isRanged() || hitInfo.isRanged) {
      const projectile = this.world.createProjectile(
        [character, target],
        hitInfo,
      );

      this.world.pushToAdjacentGroups(
        character.group,
        new Messages.Projectile(
          Packets.ProjectileOpcode.Create,
          projectile.getData(),
        ),
      );
    } else {
      this.world.pushBroadcast(
        new Messages.Combat(
          Packets.CombatOpcode.Hit,
          character.instance,
          target.instance,
          hitInfo,
        ),
      );
      this.world.handleDamage(character, target, hitInfo.damage);
    }

    if (character.damageCallback) character.damageCallback(target, hitInfo);

    this.lastHit = this.getTime();
  }

  follow(character, target) {
    this.world.pushBroadcast(
      new Messages.Movement(Packets.MovementOpcode.Follow, [
        character.instance,
        target.instance,
        character.isRanged(),
        character.attackRange,
      ]),
    );
  }

  end() {
    this.world.pushBroadcast(
      new Messages.Combat(
        Packets.CombatOpcode.Finish,
        this.character.instance,
        null,
      ),
    );
  }

  sendFollow() {
    if (!this.character.hasTarget() || this.character.target.isDead()) return;

    const ignores = [this.character.instance, this.character.target.instance];

    this.world.pushSelectively(
      new Messages.Movement(Packets.MovementOpcode.Follow, [
        this.character.instance,
        this.character.target.instance,
      ]),
      ignores,
    );
  }

  forEachAttacker(callback) {
    _.each(this.attackers, (attacker) => {
      callback(attacker);
    });
  }

  onForget(callback) {
    this.forgetCallback = callback;
  }

  targetOutOfBounds() {
    if (!this.character.hasTarget() || !this.isMob()) {
      return true;
    }

    const {
      spawnPoint,
      target,
    } = this.character;

    return (
      Utils.getDistance(spawnPoint[0], spawnPoint[1], target.x, target.y)
      > this.character.spawnDistance
    );
  }

  getTime() {
    return new Date().getTime();
  }

  colliding(x, y) {
    return this.world.map.isColliding(x, y);
  }

  isPlayer() {
    return this.character.type === 'player';
  }

  isMob() {
    return this.character.type === 'mob';
  }

  isTargetMob() {
    return this.character.target.type === 'mob';
  }

  canAttackAoE(target) {
    return (
      this.isMob()
      || target.type === 'mob'
      || (this.isPlayer()
        && target.type === 'player'
        && target.pvp
        && this.character.pvp)
    );
  }
}

export default class CombatQueue {
  constructor() {
    this.hitQueue = [];
  }

  add(hit) {
    this.hitQueue.push(hit);
  }

  hasQueue() {
    return this.hitQueue.length > 0;
  }

  clear() {
    this.hitQueue = [];
  }

  getHit() {
    if (this.hitQueue.length < 1) {
      return null;
    }

    const hit = this.hitQueue.shift();

    return hit.getData();
  }
}

export default class Hit {
  constructor(type, damage) {
    this.type = type;
    this.damage = damage;

    this.ranged = false;
    this.aoe = false;
    this.terror = false;
  }

  isRanged() {
    return this.ranged;
  }

  isAoE() {
    return this.aoe;
  }

  getDamage() {
    return this.damage;
  }

  getData() {
    return {
      type: this.type,
      damage: this.damage,
      isRanged: this.isRanged(),
      isAoE: this.isAoE(),
      hasTerror: this.terror,
    };
  }
}

import _ from 'underscore';
import Character from '../character';
import MobsDictionary from '../../../../util/mobs';
import Utils from '../../../../util/utils';
import ItemsDictionary from '../../../../util/items';

export default class Mob extends Character {
  constructor(id, instance, x, y) {
    super(id, 'mob', instance, x, y);

    if (!MobsDictionary.exists(id)) {
      log.notice('Mob not found', id, instance, x, y);
      return;
    }

    this.data = MobsDictionary.exists(this.id);
    this.hitPoints = this.data.hitPoints;
    this.maxHitPoints = this.data.hitPoints;
    this.drops = this.data.drops;

    this.respawnDelay = this.data.spawnDelay;

    this.level = this.data.level;

    this.armourLevel = this.data.armour;
    this.weaponLevel = this.data.weapon;
    this.attackRange = this.data.attackRange;
    this.aggroRange = this.data.aggroRange;
    this.aggressive = this.data.aggressive;

    this.spawnLocation = [x, y];

    this.dead = false;
    this.boss = false;
    this.static = false;

    this.projectileName = this.getProjectileName();
  }

  refresh() {
    this.hitPoints = this.data.hitPoints;
    this.maxHitPoints = this.data.hitPoints;

    if (this.refreshCallback) this.refreshCallback();
  }

  getDrop() {
    if (!this.drops) {
      return null;
    }

    let min = 0;
    let percent = 0;
    const random = Utils.randomInt(0, 1000);

    for (const drop in this.drops) {
      if (this.drops.hasOwnProperty(drop)) {
        const chance = this.drops[drop];

        min = percent;
        percent += chance;

        if (random >= min && random < percent) {
          let count = 1;

          if (drop === 'gold') {
            count = Utils.randomInt(
              1,
              this.level
              * Math.floor(Math.pow(2, this.level / 7) / (this.level / 4)),
            );
          }

          return {
            id: ItemsDictionary.stringToId(drop),
            count,
          };
        }
      }
    }

    return null;
  }

  getProjectileName() {
    return this.data.projectileName
      ? this.data.projectileName
      : 'projectile-pinearrow';
  }

  canAggro(player) {
    if (
      this.hasTarget()
      || !this.aggressive
      || Math.floor(this.level * 1.5) < player.level
    ) return false;

    return this.isNear(player, this.aggroRange);
  }

  destroy() {
    this.dead = true;
    this.clearTarget();
    this.resetPosition();
    this.respawn();

    if (this.area) {
      this.area.removeEntity(this);
    }
  }

  return() {
    this.clearTarget();
    this.resetPosition();
    this.move(this.x, this.y);
  }

  isRanged() {
    return this.attackRange > 1;
  }

  distanceToSpawn() {
    return this.getCoordDistance(this.spawnLocation[0], this.spawnLocation[1]);
  }

  isAtSpawn() {
    return this.x === this.spawnLocation[0] && this.y === this.spawnLocation[1];
  }

  isOutsideSpawn() {
    return this.distanceToSpawn() > this.spawnDistance;
  }

  addToChestArea(chestAreas) {
    const area = _.find(chestAreas, coords => coords.contains(this.x, this.y));

    if (area) {
      area.addEntity(this);
    }
  }

  respawn() {
    /**
     * Some entities are static (only spawned once during an event)
     * Meanwhile, other entities act as an illusion to another entity,
     * so the resawning script is handled elsewhere.
     */

    if (!this.static || this.respawnDelay === -1) {
      return;
    }

    setTimeout(() => {
      if (this.respawnCallback) {
        this.respawnCallback();
      }
    }, this.respawnDelay);
  }

  getState() {
    const base = super.getState();

    base.hitPoints = this.hitPoints;
    base.maxHitPoints = this.maxHitPoints;
    base.attackRange = this.attackRange;
    base.level = this.level;

    return base;
  }

  resetPosition() {
    this.setPosition(this.spawnLocation[0], this.spawnLocation[1]);
  }

  onRespawn(callback) {
    this.respawnCallback = callback;
  }

  onMove(callback) {
    this.moveCallback = callback;
  }

  onReturn(callback) {
    this.returnCallback = callback;
  }

  onRefresh(callback) {
    this.refreshCallback = callback;
  }

  onDeath(callback) {
    this.deathCallback = callback;
  }

  move(x, y) {
    this.setPosition(x, y);

    if (this.moveCallback) {
      this.moveCallback(this);
    }
  }
}

import _ from 'underscore';

export default class Abilities {
  constructor(player) {
    this.player = player;

    this.abilities = {};
    this.shortcuts = [];

    this.shortcutSize = 5;
  }

  addAbility(ability) {
    this.abilities[ability.name] = ability;
  }

  addShortcut(ability) {
    if (this.shortcutSize >= 5) {
      return;
    }

    this.shortcuts.push(ability.name);
  }

  removeAbility(ability) {
    if (this.isShortcut(ability)) {
      this.removeShortcut(this.shortcuts.indexOf(ability.name));
    }

    delete this.abilities[ability.name];
  }

  removeShortcut(index) {
    if (index > -1) {
      this.shortcuts.splice(index, 1);
    }
  }

  hasAbility(ability) {
    _.each(this.abilities, (uAbility) => {
      if (uAbility.name === ability.name) {
        return true;
      }
      return false;
    });

    return false;
  }

  isShortcut(ability) {
    return this.shortcuts.indexOf(ability.name) > -1;
  }

  getArray() {
    let abilities = '';
    let abilityLevels = '';
    const shortcuts = this.shortcuts.toString();

    _.each(this.abilities, (ability) => {
      abilities += ability.name;
      abilityLevels += ability.level;
    });

    return {
      username: this.player.username,
      abilities,
      abilityLevels,
      shortcuts,
    };
  }
}

