export default class Slot extends Container {
  constructor(owner, size) {
    super('Bank', owner, size);
    this.open = false;
  }

  load(ids, counts, abilities, abilityLevels) {
    super.load(ids, counts, abilities, abilityLevels);

    this.owner.send(
      new Messages.Bank(Packets.BankOpcode.Batch, [this.size, this.slots]),
    );
  }

  add(id, count, ability, abilityLevel) {
    if (!this.canHold(id, count)) {
      this.owner.send(
        new Messages.Notification(
          Packets.NotificationOpcode.Text,
          'You do not have enough space in your bank.',
        ),
      );
      return false;
    }

    const slot = super.add(id, parseInt(count, 10), ability, abilityLevel);

    this.owner.send(new Messages.Bank(Packets.BankOpcode.Add, slot));
    this.owner.save();

    return true;
  }

  remove(id, count, index) {
    if (!super.remove(index, id, count)) {
      return;
    }

    this.owner.send(
      new Messages.Bank(Packets.BankOpcode.Remove, {
        index: parseInt(index, 10),
        count,
      }),
    );

    this.owner.save();
  }
}

import _ from 'underscore';
import log from '../../../../../util/log';
import Slot from './slot';
import Items from '../../../../../util/items';
import Constants from '../../../../../util/constants';

export default class Container {
  constructor(type, owner, size) {
    this.type = type;
    this.owner = owner;
    this.size = size;
    this.slots = [];

    for (let i = 0; i < this.size; i += 1) {
      this.slots.push(new Slot(i));
    }
  }

  /**
   * Fill each slot with manual data or the database
   */
  load(ids, counts, abilities, abilityLevels) {
    if (ids.length !== this.slots.length) {
      log.error(`[${this.type}] Mismatch in container size.`);
    }

    for (let i = 0; i < this.slots.length; i += 1) {
      this.slots[i].load(ids[i], counts[i], abilities[i], abilityLevels[i]);
    }
  }

  loadEmpty() {
    const
      data = [];

    /**
     * Better to have it condensed into one.
     */

    for (let i = 0; i < this.size; i += 1) data.push(-1);

    this.load(data, data, data, data);
  }

  add(id, count, ability, abilityLevel) {
    const maxStackSize = Items.maxStackSize(id) === -1
      ? Constants.MAX_STACK
      : Items.maxStackSize(id);

    if (!id || count < 0 || count > maxStackSize) {
      return null;
    }

    if (!Items.isStackable(id)) {
      if (this.hasSpace()) {
        const nsSlot = this.slots[this.getEmptySlot()]; // non-stackable slot

        nsSlot.load(id, count, ability, abilityLevel);

        return nsSlot;
      }
    } else if (maxStackSize === -1 || this.type === 'Bank') {
      const sSlot = this.getSlot(id);

      if (sSlot) {
        sSlot.increment(count);
        return sSlot;
      }
      if (this.hasSpace()) {
        const slot = this.slots[this.getEmptySlot()];

        slot.load(id, count, ability, abilityLevel);

        return slot;
      }
    } else {
      let remainingItems = count;

      for (let i = 0; i < this.slots.length; i += 1) {
        if (this.slots[i].id === id) {
          const rSlot = this.slots[i];

          const available = maxStackSize - rSlot.count;

          if (available >= remainingItems) {
            rSlot.increment(remainingItems);

            return rSlot;
          }
          if (available > 0) {
            rSlot.increment(available);
            remainingItems -= available;
          }
        }
      }

      if (remainingItems > 0 && this.hasSpace()) {
        const rrSlot = this.slots[this.getEmptySlot()];

        rrSlot.load(id, remainingItems, ability, abilityLevel);

        return rrSlot;
      }
    }

    return null;
  }

  canHold(id, count) {
    if (!Items.isStackable(id)) {
      return this.hasSpace();
    }

    if (this.hasSpace()) {
      return true;
    }

    const maxStackSize = Items.maxStackSize(id);

    if ((this.type === 'Bank' || maxStackSize === -1) && this.contains(id)) {
      return true;
    }

    if (maxStackSize !== -1 && count > maxStackSize) {
      return false;
    }

    let remainingSpace = 0;

    for (let i = 0; i < this.slots.length; i += 1) {
      if (this.slots[i].id === id) {
        remainingSpace += maxStackSize - this.slots[i].count;
      }
    }

    return remainingSpace >= count;
  }

  remove(index, id, count) {
    if (
      !id
      || count < 0
      || !this.contains(id)
      || !this.slots[index]
      || this.slots[index].id === -1
      || this.slots[index].id !== id
    ) return false;

    const slot = this.slots[index];

    if (Items.isStackable(id)) {
      if (count >= slot.count) slot.empty();
      else slot.decrement(count);
    } else slot.empty();

    return true;
  }

  getSlot(id) {
    for (let i = 0; i < this.slots.length; i += 1) {
      if (this.slots[i].id === id) {
        return this.slots[i];
      }
    }

    return null;
  }

  contains(id) {
    for (let i = 0; i < this.slots.length; i += 1) {
      if (this.slots[i].id === id) {
        return true;
      }
    }

    return false;
  }

  containsSpaces(count) {
    const emptySpaces = [];

    for (let i = 0; i < this.slots.length; i += 1) {
      if (this.slots[i].id === -1) {
        emptySpaces.push(this.slots[i]);
      }
    }

    return emptySpaces.length === count;
  }

  hasSpace() {
    return this.getEmptySlot() > -1;
  }

  getEmptySlot() {
    for (let i = 0; i < this.slots.length; i += 1) if (this.slots[i].id === -1) return i;

    return -1;
  }

  getIndex(id) {
    /**
     * Used when the index is not determined,
     * returns the first item found based on the id.
     */

    for (let i = 0; i < this.slots.length; i += 1) {
      if (this.slots[i].id === id) {
        return i;
      }
    }

    return -1;
  }

  check() {
    _.each(this.slots, (slot) => {
      if (isNaN(slot.id)) {
        slot.empty();
      }
    });
  }

  getArray() {
    let
      ids = '';


    let counts = '';


    let abilities = '';


    let abilityLevels = '';

    for (let i = 0; i < this.slots.length; i += 1) {
      ids += `${this.slots[i].id} `;
      counts += `${this.slots[i].count} `;
      abilities += `${this.slots[i].ability} `;
      abilityLevels += `${this.slots[i].abilityLevel} `;
    }

    return {
      username: this.owner.username,
      ids: ids.slice(0, -1),
      counts: counts.slice(0, -1),
      abilities: abilities.slice(0, -1),
      abilityLevels: abilityLevels.slice(0, -1),
    };
  }
}

export default {
  InventoryFull: 'You do not have enough space in your inventory!',
  NoDrop: 'You cannot drop an item here.',
};

import Container from '../container';
import Messages from '../../../../../../network/messages';
import Packets from '../../../../../../network/packets';
import Constants from './constants';

/**
 * Not particularly sure whether or not this class will
 * require an update function to push any updates
 * of the inventory to the client. This is just a baseline
 * setup for the inventory until the interface is done.
 */
export default class Inventory extends Container {
  constructor(owner, size) {
    super('Inventory', owner, size);
  }

  load(ids, counts, abilities, abilityLevels) {
    super.load(ids, counts, abilities, abilityLevels);

    this.owner.send(
      new Messages.Inventory(Packets.InventoryOpcode.Batch, [
        this.size,
        this.slots,
      ]),
    );
  }

  add(item, count) {
    if (!count) {
      count = -1; // eslint-disable-line
    }

    // default to moving whole stack
    if (count === -1) {
      count = parseInt(item.count, 10); // eslint-disable-line
    }

    if (!this.canHold(item.id, count)) {
      this.owner.send(
        new Messages.Notification(
          Packets.NotificationOpcode.Text,
          Constants.InventoryFull,
        ),
      );
      return false;
    }

    const slot = super.add(item.id, count, item.ability, item.abilityLevel);

    if (!slot) {
      return false;
    }

    this.owner.send(new Messages.Inventory(Packets.InventoryOpcode.Add, slot));
    this.owner.save();

    if (item.instance) {
      this.owner.world.removeItem(item);
    }

    return true;
  }

  remove(id, count, index) {
    if (!index) {
      index = this.getIndex(id); // eslint-disable-line
    }

    if (!super.remove(index, id, count)) {
      return;
    }

    this.owner.send(
      new Messages.Inventory(Packets.InventoryOpcode.Remove, {
        index: parseInt(index, 10),
        count,
      }),
    );

    this.owner.save();
  }
}

import Items from '../../../../../util/items';

export default class Slot {
  constructor(index) {
    this.index = index;
    this.id = -1;
    this.count = -1;
    this.ability = -1;
    this.abilityLevel = -1;
    this.name = null;
  }

  load(id, count, ability, abilityLevel) {
    this.id = parseInt(id, 10);
    this.count = parseInt(count, 10);
    this.ability = parseInt(ability, 10);
    this.abilityLevel = parseInt(abilityLevel, 10);

    this.name = Items.idToString(this.id);
    this.edible = Items.isEdible(this.id);
    this.equippable = Items.isEquippable(this.name);

    this.verify();
  }

  empty() {
    this.id = -1;
    this.count = -1;
    this.ability = -1;
    this.abilityLevel = -1;
    this.name = null;
  }

  increment(amount) {
    this.count += parseInt(amount, 10);
    this.verify();
  }

  decrement(amount) {
    this.count -= parseInt(amount, 10);
    this.verify();
  }

  verify() {
    if (isNaN(this.count)) {
      this.count = 1;
    }
  }

  getData() {
    return {
      index: this.index,
      name: this.name,
      count: this.count,
      ability: this.ability,
      abilityLevel: this.abilityLevel,
    };
  }
}

import Items from '../../../../../util/items';
import Messages from '../../../../../network/messages';
import Packets from '../../../../../network/packets';
import Utils from '../../../../../util/utils';

/**
 * Tier 1 - Damage/Armour boost (1-5%)
 * Tier 2 - Damage boost (1-10% & 10% for special ability or special ability level up)
 * Tier 3 - Damage boost (1-15% & 15% for special ability or special ability level up)
 * Tier 4 - Damage boost (1-20% & 20% for special ability or special ability level up)
 * Tier 5 - Damage boost (1-40% & 25% for special ability or special ability level up)
 */
export default class Enchant {
  constructor(player) {
    this.player = player;
    this.selectedItem = null;
    this.selectedShards = null;
  }

  convert(shard) {
    if (!Items.isShard(shard.id) || !this.player.inventory.hasSpace()) {
      return;
    }

    const tier = Items.getShardTier(shard.id);

    if (shard.count < 11 && tier > 5) {
      return;
    }

    for (let i = 0; i < shard.count; i += 10) {
      this.player.inventory.remove(shard.id, 10, shard.index);

      this.player.inventory.add({
        id: shard.id + 1,
        count: 1,
        ability: -1,
        abilityLevel: -1,
      });
    }
  }

  enchant() {
    if (!this.selectedItem) {
      this.player.notify('You have not selected an item to enchant.');
      return;
    }

    if (!this.selectedShards) {
      this.player.notify('You have to select shards to infuse.');
      return;
    }

    if (!this.verify()) {
      this.player.notify('This item cannot be enchanted.');
      return;
    }

    if (this.selectedShards.count < 10) {
      this.player.notify('You must have a minimum of 10 shards to enchant.');
      return;
    }

    /**
     * Implement probabilities here based on the number of shards
     * and reason them out.
     */

    const {
      tier,
    } = this.selectedItem;

    this.selectedItem.count = Utils.randomInt(1, tier === 5 ? 40 : 5 * tier);

    if (tier < 2) return;

    if (this.hasAbility(this.selectedItem)) {
      if (this.selectedItem.abilityLevel < 5) this.selectedItem.abilityLevel += 1;
      else this.generateAbility();
    }

    this.player.inventory.remove(
      this.selectedShards.id,
      10,
      this.selectedShards.index,
    );
    this.player.sync();
  }

  generateAbility() {
    const
      type = Items.getType(this.selectedItem.id);


    const probability = Utils.randomInt(0, 100);

    if (probability > 5 + 5 * this.selectedShards.tier) return;

    switch (type) {
      default:
        break;
      case 'armor':
      case 'armorarcher':
        this.selectedItem.ability = Utils.randomInt(2, 3);
        break;

      case 'weapon':
        this.selectedItem.ability = Utils.randomInt(0, 1);
        break;

      case 'weaponarcher':
        this.selectedItem.ability = Utils.randomInt(4, 5);
        break;

      case 'pendant':
        break;

      case 'ring':
        break;

      case 'boots':
        break;
    }
  }

  verify() {
    return (
      Items.isEnchantable(this.selectedItem.id)
      && Items.isShard(this.selectedShards.id)
    );
  }

  add(type, item) {
    const isItem = item === 'item';

    if (isItem && !Items.isEnchantable(item.id)) {
      return;
    }

    if (type === 'item') {
      if (this.selectedItem) {
        this.remove('item');
      }

      this.selectedItem = item;
    } else if (type === 'shards') {
      if (this.selectedShards) {
        this.remove('shards');
      }

      this.selectedShards = item;
    }

    this.player.send(
      new Messages.Enchant(Packets.EnchantOpcode.Select, {
        type,
        index: item.index,
      }),
    );
  }

  remove(type) {
    let index;

    if (type === 'item' && this.selectedItem) {
      index = this.selectedItem.index;

      this.selectedItem = null;
    } else if (type === 'shards' && this.selectedShards) {
      index = this.selectedShards.index;

      this.selectedShards = null;
    }

    this.player.send(
      new Messages.Enchant(Packets.EnchantOpcode.Remove, {
        type,
        index,
      }),
    );
  }

  hasAbility(item) {
    return item.ability !== -1;
  }
}

