export default class Entity {
  constructor(id, type, instance, x, y) {
    this.id = id;
    this.type = type;
    this.instance = instance;
    this.oldX = x;
    this.oldY = y;
    this.x = x;
    this.y = y;

    this.combat = null;

    this.dead = false;

    this.recentGroups = [];
  }

  getCombat() {
    return null;
  }

  getDistance(entity) {
    const
      x = Math.abs(this.x - entity.x);


    const y = Math.abs(this.y - entity.y);

    return x > y ? x : y;
  }

  getCoordDistance(toX, toY) {
    const
      x = Math.abs(this.x - toX);


    const y = Math.abs(this.y - toY);

    return x > y ? x : y;
  }

  isAdjacent(entity) {
    if (!entity) return false;

    return this.getDistance(entity) <= 1;
  }

  isNonDiagonal(entity) {
    return (
      this.isAdjacent(entity) && !(entity.x !== this.x && entity.y !== this.y)
    );
  }

  isNear(entity, distance) {
    let
      near = false;

    const dx = Math.abs(this.x - entity.x);


    const dy = Math.abs(this.y - entity.y);

    if (dx <= distance && dy <= distance) near = true;

    return near;
  }

  talk() {
    log.notice('Who is screwing around with the client?');
  }

  drop(item) {
    return new Messages.Drop(this, item);
  }

  isPlayer() {
    return this.type === 'player';
  }

  isMob() {
    return this.type === 'mob';
  }

  isNPC() {
    return this.type === 'npc';
  }

  isItem() {
    return this.type === 'item';
  }

  setPosition(x, y) {
    this.x = x;
    this.y = y;

    if (this.setPositionCallback) this.setPositionCallback();
  }

  hasSpecialAttack() {
    return false;
  }

  updatePosition() {
    this.oldX = this.x;
    this.oldY = this.y;
  }

  onSetPosition(callback) {
    this.setPositionCallback = callback;
  }

  getState() {
    const isNPC = this.isNPC()
      ? NPCs.idToString(this.id)
      : Items.idToString(this.id);

    const string = this.isMob()
      ? Mobs.idToString(this.id)
      : isNPC;

    const isMobNPC = this.isNPC()
      ? NPCs.idToName(this.id)
      : Items.idToName(this.id);

    const name = this.isMob()
      ? Mobs.idToName(this.id)
      : isMobNPC;

    return {
      type: this.type,
      id: this.instance,
      string,
      name: string,
      label: name,
      x: this.x,
      y: this.y,
    };
  }
}

import Entity from '../entity';

export default class NPC extends Entity {
  constructor(id, instance, x, y) {
    super(id, 'npc', instance, x, y);
    this.talkIndex = 0;
  }

  talk(messages) {
    if (this.talkIndex > messages.length) {
      this.talkIndex = 0;
    }

    this.talkIndex += 1;
  }
}

import Entity from '../entity';
import Utils from '../../../util/utils';

export default class Chest extends Entity {
  constructor(id, instance, x, y) {
    super(id, 'chest', instance, x, y);

    this.respawnDuration = 25000; // Every 25 seconds
    this.static = false;

    this.items = [];
  }

  openChest() {
    if (this.openCallback) {
      this.openCallback();
    }
  }

  respawn() {
    setTimeout(() => {
      if (this.respawnCallback) this.respawnCallback();
    }, this.respawnDuration);
  }

  getItem() {
    const random = Utils.randomInt(0, this.items.length - 1);
    const item = this.items[random];

    if (!item) {
      return null;
    }

    return item;
  }

  onOpen(callback) {
    this.openCallback = callback;
  }

  onRespawn(callback) {
    this.respawnCallback = callback;
  }
}

import Entity from '../entity';

export default class Item extends Entity {
  constructor(id, instance, x, y) {
    super(id, 'item', instance, x, y);

    this.static = false;
    this.dropped = false;
    this.shard = false;
    this.count = 1;
    this.ability = 0;
    this.abilityLevel = 0;
    this.tier = 1;
    this.respawnTime = 30000;
    this.despawnDuration = 4000;
    this.blinkDelay = 20000;
    this.despawnDelay = 1000;
    this.blinkTimeout = null;
    this.despawnTimeout = null;
  }

  destroy() {
    if (this.blinkTimeout) {
      clearTimeout(this.blinkTimeout);
    }

    if (this.despawnTimeout) {
      clearTimeout(this.despawnTimeout);
    }

    if (this.static) {
      this.respawn();
    }
  }

  despawn() {
    this.blinkTimeout = setTimeout(() => {
      if (this.blinkCallback) this.blinkCallback();

      this.despawnTimeout = setTimeout(() => {
        if (this.despawnCallback) this.despawnCallback();
      }, this.despawnDuration);
    }, this.blinkDelay);
  }

  respawn() {
    setTimeout(() => {
      if (this.respawnCallback) {
        this.respawnCallback();
      }
    }, this.respawnTime);
  }

  getData() {
    return [this.id, this.count, this.ability, this.abilityLevel];
  }

  getState() {
    const state = super.getState();

    state.count = this.count;
    state.ability = this.ability;
    state.abilityLevel = this.abilityLevel;

    return state;
  }

  setCount(count) {
    this.count = count;
  }

  setAbility(ability) {
    this.ability = ability;
  }

  setAbilityLevel(abilityLevel) {
    this.abilityLevel = abilityLevel;
  }

  onRespawn(callback) {
    this.respawnCallback = callback;
  }

  onBlink(callback) {
    this.blinkCallback = callback;
  }

  onDespawn(callback) {
    this.despawnCallback = callback;
  }
}

import Entity from '../entity';

export default class Projectile extends Entity {
  constructor(id, instance) {
    super(id, 'projectile', instance);

    this.startX = -1;
    this.startY = -1;
    this.destX = -1;
    this.destY = -1;
    this.target = null;
    this.damage = -1;
    this.hitType = null;
    this.owner = null;
  }

  setStart(x, y) {
    this.x = x;
    this.y = y;
  }

  setTarget(target) {
    this.target = target;

    this.destX = target.x;
    this.destY = target.y;
  }

  setStaticTarget(x, y) {
    this.static = true;

    this.destX = x;
    this.destY = y;
  }

  getData() {
    /**
     * Cannot generate a projectile
     * unless it has a target.
     */

    if (!this.owner || !this.target) {
      return null;
    }

    return {
      id: this.instance,
      name: this.owner.projectileName,
      characterId: this.owner.instance,
      targetId: this.target.instance,
      damage: this.damage,
      special: this.special,
      hitType: this.hitType,
      type: this.type,
    };
  }
}

import Utils from '../util/utils';

const LevelExp = [];

const getMaxDamage = (attacker, target) => {
  if (!attacker || !target) {
    return 0;
  }

  let damageDealt;
  let damageAbsorbed;
  let damageAmplifier = 1;
  let absorptionAmplifier = 1;

  const usingRange = attacker.weapon
    ? attacker.weapon.isRanged()
    : attacker.isRanged();

  const weaponLevel = attacker.weapon
    ? attacker.weapon.getLevel()
    : attacker.weaponLevel;

  const armourLevel = attacker.armour
    ? attacker.armour.getDefense()
    : attacker.armourLevel;

  const pendant = attacker.pendant ? attacker.pendant : null;
  const ring = attacker.ring ? attacker.ring : null;
  const boots = attacker.boots ? attacker.boots : null;

  const targetArmour = target.armour
    ? target.armour.getDefense()
    : target.armourLevel;

  const targetPendant = target.pendant ? target.pendant : null;
  const targetRing = target.ring ? target.ring : null;
  const targetBoots = target.boots ? target.boots : null;
  const isPlayer = attacker.type === 'player';

  damageDealt = (isPlayer ? 10 : 0)
    + attacker.level
    + (attacker.level * weaponLevel) / 4
    + (attacker.level + weaponLevel * armourLevel) / 8;

  /**
   * Apply ranged damage deficit
   */

  if (usingRange) {
    damageDealt /= 1.275;
  }

  /**
   * Apply special amulets
   */

  if (pendant && pendant.pendantLevel > 0) {
    damageAmplifier *= pendant.getBaseAmplifier();
  }

  if (ring && ring.ringLevel > 0) {
    damageAmplifier *= ring.getBaseAmplifier();
  }

  if (boots && boots.bootsLevel > 0) {
    damageAmplifier *= boots.getBaseAmplifier();
  }

  /**
   * Just so amplifiers don't get out of hand.
   */

  if (damageAmplifier > 1.6) {
    damageAmplifier = 1.6;
  }

  damageDealt *= damageAmplifier;

  damageAbsorbed = target.level + targetArmour / 2;

  if (targetPendant) {
    absorptionAmplifier *= targetPendant.getBaseAmplifier();
  }

  if (targetRing) {
    absorptionAmplifier *= targetRing.getBaseAmplifier();
  }

  if (targetBoots) {
    absorptionAmplifier *= targetBoots.getBaseAmplifier();
  }

  damageAbsorbed *= absorptionAmplifier;

  let damage = damageDealt - damageAbsorbed;

  damage = Math.ceil(damage);

  if (isNaN(damage) || !damage || damage < 0) {
    damage = 0;
  }

  return damage;
};

const getDamage = (attacker, target, special) => {
  const maxDamage = getMaxDamage(attacker, target, special);
  const accuracy = Utils.randomInt(0, attacker.level);
  return Utils.randomInt(accuracy, maxDamage);
};

/**
 * The critical is the player's max hit plus *= critical multiplier of the weapon
 */
const getCritical = (attacker, target) => {
  if (!attacker || !target) {
    return 0;
  }

  let damage = getDamage(attacker, target);
  const multiplier = attacker.weapon.abilityLevel / 10;
  damage *= multiplier;

  return damage;
};

const getWeaponBreak = (attacker, target) => {
  if (!attacker || !target) {
    return false;
  }

  const targetArmour = target.getArmourLevel();
  const breakOnArmour = 100 - (targetArmour * 10);

  /**
   * The chance a weapon will break ....
   * based upon the 100 - (armourLevel * 10)
   * this will never be lower than 35
   */
  const breakChance = Utils.randomRange(1, (breakOnArmour > 35 || 35));

  return breakChance < 25;
};

/**
 * Preliminary setup until this function is expanded
 * and fits in the necessary algorithms.
 */
const getAoEDamage = (attacker, target) => getDamage(attacker, target);

const expToLevel = (experience) => {
  if (experience < 0) {
    return -1;
  }

  for (let i = 1; i < LevelExp.length; i += 1) {
    if (experience < LevelExp[i]) {
      return i;
    }
  }

  return -1;
};

const getMaxHitPoints = level => 100 + level * 30;

const getMaxMana = level => 10 + level * 8;

export default {
  LevelExp,
  getDamage,
  getMaxDamage,
  getCritical,
  getWeaponBreak,
  getMaxHitPoints,
  getMaxMana,
  expToLevel,
  getAoEDamage,
};

import _ from 'underscore';
import log from '../util/log';
import config from '../../config.json';
import Player from './entity/character/player/player';
import Map from '../map/map';
import Messages from '../network/messages';
import Utils from '../util/utils';
import MobsDictionary from '../util/mobs';
import Mob from './entity/character/mob/mob';
import NpcsDictionary from '../util/npcs';
import NPC from './entity/npc/npc';
import ItemsDictionary from '../util/items';
import Item from './entity/objects/item';
import Chest from './entity/objects/chest';
import Character from './entity/character/character';
import Projectile from './entity/objects/projectile';
import Packets from '../network/packets';
import Formulas from './formulas';
import Modules from '../util/modules';

export default class World {
  constructor(id, socket, database) {
    this.id = id;
    this.socket = socket;
    this.database = database;
    this.playerCount = 0;
    this.maxPlayers = config.maxPlayers;
    this.updateTime = config.updateTime;
    this.debug = false;

    this.players = {};
    this.entities = {};
    this.chests = {};
    this.itemsDictionary = ItemsDictionary;
    this.mobsDictionary = MobsDictionary;
    this.npcsDictionary = NpcsDictionary;
    this.projectiles = {};
    this.packets = {};
    this.groups = {};

    this.loadedGroups = false;
    this.ready = false;
    this.malformTimeout = null;

    this.onPlayerConnection((connection) => {
      if (config.development) {
        connection.sendUTF8('maintenance');
        connection.close();

        return;
      }

      const clientId = Utils.generateClientId();
      const player = new Player(this, this.database, connection, clientId);
      const diff = new Date().getTime()
        - this.socket.ips[connection.socket.conn.remoteAddress];

      if (diff < 4000) {
        connection.sendUTF8('toofast');
        if (connection.Socket) {
          connection.Socket.close('Logging in too rapidly');
        }

        return;
      }

      this.socket.ips[
        connection.socket.conn.remoteAddress
      ] = new Date().getTime();

      this.addToPackets(player);

      this.pushToPlayer(
        player,
        new Messages.Handshake(clientId, config.devClient),
      );
    });

    /**
     * Grab the exact number of players from
     * the array of packets instead of adding
     * and subtracting and risking uncertainties.
     */
    this.onPopulationChange(() => {
      this.pushBroadcast(new Messages.Population(this.getPopulation()));
    });
  }

  /**
   * The reason maps are loaded per each world is because
   * we can have slight modifications for each world if we want in the
   * future. Using region loading, we can just send the client
   * whatever new map we have created server sided. Cleaner and nicer.
   */
  load(onWorldLoad) {
    log.notice(`************ World ${this.id} ***********`);

    this.map = new Map(this);
    this.map.isReady(() => {
      this.loadGroups();
      this.spawnChests();
      this.spawnEntities();

      log.notice('The map has been successfully loaded!');

      this.loaded();
      onWorldLoad();
    });
  }

  /**
   * Similar to TTA engine here, but it's loaded upon initialization
   * rather than being called from elsewhere.
   */
  loaded() {
    this.tick();

    if (!config.offlineMode) {
      this.dataParser();
    }

    this.ready = true;
    log.notice('********************************');
  }

  tick() {
    setInterval(() => {
      this.parsePackets();
      this.parseGroups();
    }, 1000 / this.updateTime);
  }

  dataParser() {
    setInterval(() => {
      this.saveAll();
    }, 30000);
  }

  parsePackets() {
    /**
     * This parses through the packet pool and sends them
     */

    for (const id in this.packets) {
      if (this.packets[id].length > 0 && this.packets.hasOwnProperty(id)) {
        const conn = this.socket.getConnection(id);

        if (conn) {
          conn.send(this.packets[id]);
          this.packets[id] = [];
          this.packets[id].id = id;
        } else {
          delete this.socket.getConnection(id);
        }
      }
    }
  }

  parseGroups() {
    if (!this.loadedGroups) {
      return;
    }

    this.map.groups.forEachGroup((groupId) => {
      if (this.groups[groupId].incoming.length < 1) {
        return;
      }

      this.sendSpawns(groupId);

      this.groups[groupId].incoming = [];
    });
  }

  /**
   * Entity related functions
   */

  kill(entity) {
    entity.applyDamage(entity.hitPoints);

    this.pushToAdjacentGroups(
      entity.group,
      new Messages.Points(entity.instance, entity.getHitPoints(), null),
    );
    this.pushToAdjacentGroups(
      entity.group,
      new Messages.Despawn(entity.instance),
    );

    this.handleDeath(entity, true);
  }

  handleDamage(attacker, target, damage) {
    if (!attacker || !target || isNaN(damage) || target.invincible) {
      return;
    }

    if (target.type === 'player' && target.hitCallback) {
      target.hitCallback(attacker, damage);
    }

    // Stop screwing with this - it's so the target retaliates.
    target.hit(attacker);
    target.applyDamage(damage);

    this.pushToAdjacentGroups(
      target.group,
      new Messages.Points(target.instance, target.getHitPoints(), null),
    );

    if (target.getHitPoints() < 1) {
      target.combat.forEachAttacker((opponent) => {
        opponent.removeTarget();

        this.pushToAdjacentGroups(
          target.group,
          new Messages.Combat(Packets.CombatOpcode.Finish, [
            opponent.instance,
            target.instance,
          ]),
        );

        if (opponent.type === 'player' || target.type === 'player') {
          if (opponent.type === 'mob') {
            opponent.addExperience(MobsDictionary.getXp(target.id));
          }

          if (opponent.type === 'player') {
            opponent.killCharacter(target);
          }
        }
      });

      this.pushToAdjacentGroups(
        target.group,
        new Messages.Despawn(target.instance),
      );
      this.handleDeath(target);
    }
  }

  handleDeath(character, ignoreDrops) {
    if (!character) {
      return;
    }

    if (character.type === 'mob') {
      const deathX = character.x;
      const deathY = character.y;

      if (character.deathCallback) {
        character.deathCallback();
      }

      this.removeEntity(character);

      character.dead = true; // eslint-disable-line
      character.destroy();
      character.combat.stop();

      if (!ignoreDrops) {
        const drop = character.getDrop();

        if (drop) this.dropItem(drop.id, drop.count, deathX, deathY);
      }
    } else if (character.type === 'player') character.die();
  }

  createProjectile(info) {
    const attacker = info.shift();
    const target = info.shift();

    if (!attacker || !target) {
      return null;
    }

    const startX = attacker.x;
    const startY = attacker.y;
    const type = attacker.getProjectile();
    let hit = null;

    const projectile = new Projectile(
      type,
      Utils.generateInstance(5, type, startX + startY),
    );

    projectile.setStart(startX, startY);
    projectile.setTarget(target);

    if (attacker.type === 'player') {
      hit = attacker.getHit(target);
    }

    projectile.damage = hit
      ? hit.damage
      : Formulas.getDamage(attacker, target, true);

    projectile.hitType = hit
      ? hit.type
      : Modules.Hits.Damage;

    projectile.owner = attacker;

    this.addProjectile(projectile);

    return projectile;
  }

  /**
   *
   * @param {int} groupId
   */
  sendSpawns(groupId) {
    if (!groupId) {
      return;
    }

    _.each(this.groups[groupId].incoming, (entity) => {
      if (entity.instance === null) {
        return;
      }

      this.pushToGroup(
        groupId,
        new Messages.Spawn(entity),
        entity.isPlayer() ? entity.instance : null,
      );
    });
  }

  loadGroups() {
    this.map.groups.forEachGroup((groupId) => {
      this.groups[groupId] = {
        entities: {},
        players: [],
        incoming: [],
      };
    });

    this.loadedGroups = true;
  }

  getEntityByInstance(instance) {
    if (instance in this.entities) {
      return this.entities[instance];
    }

    return null;
  }

  /**
   * Important functions for sending
   * messages to the player(s)
   */

  pushBroadcast(message) {
    _.each(this.packets, (packet) => {
      packet.push(message.serialize());
    });
  }

  pushSelectively(message, ignores) {
    _.each(this.packets, (packet) => {
      if (ignores.indexOf(packet.id) < 0) {
        packet.push(message.serialize());
      }
    });
  }

  pushToPlayer(player, message) {
    if (player && player.instance in this.packets) {
      this.packets[player.instance].push(message.serialize());
    }
  }

  pushToGroup(id, message, ignoreId) {
    const
      group = this.groups[id];

    if (!group) return;

    _.each(group.players, (playerId) => {
      if (playerId !== ignoreId) {
        this.pushToPlayer(this.getEntityByInstance(playerId), message);
      }
    });
  }

  pushToAdjacentGroups(groupId, message, ignoreId) {
    this.map.groups.forEachAdjacentGroup(groupId, (id) => {
      this.pushToGroup(id, message, ignoreId);
    });
  }

  pushToOldGroups(player, message) {
    _.each(player.recentGroups, (id) => {
      this.pushToGroup(id, message);
    });

    player.recentGroups = []; // eslint-disable-line
  }

  addToGroup(entity, groupId) {
    const
      newGroups = [];

    if (entity && groupId && groupId in this.groups) {
      this.map.groups.forEachAdjacentGroup(groupId, (id) => {
        const group = this.groups[id];

        if (group && group.entities) {
          group.entities[entity.instance] = entity;
          newGroups.push(id);
        }
      });

      entity.group = groupId; // eslint-disable-line

      if (entity instanceof Player) {
        this.groups[groupId].players.push(entity.instance);
      }
    }

    return newGroups;
  }

  removeFromGroups(entity) {
    const oldGroups = [];

    if (entity && entity.group) {
      const group = this.groups[entity.group];

      if (entity instanceof Player) {
        group.players = _.reject(group.players, id => id === entity.instance);
      }

      this.map.groups.forEachAdjacentGroup(entity.group, (id) => {
        if (this.groups[id] && entity.instance in this.groups[id].entities) {
          delete this.groups[id].entities[entity.instance];
          oldGroups.push(id);
        }
      });

      entity.group = null; // eslint-disable-line
    }

    return oldGroups;
  }

  incomingToGroup(entity, groupId) {
    if (!entity || !groupId) {
      return;
    }

    this.map.groups.forEachAdjacentGroup(groupId, (id) => {
      const group = this.groups[id];

      if (group && !_.include(group.entities, entity.instance)) {
        group.incoming.push(entity);
      }
    });
  }

  handleEntityGroup(entity) {
    let groupsChanged = false;

    if (!entity) {
      return groupsChanged;
    }

    const groupId = this.map.groups.groupIdFromPosition(entity.x, entity.y);

    if (!entity.group || (entity.group && entity.group !== groupId)) {
      groupsChanged = true;

      this.incomingToGroup(entity, groupId);

      const oldGroups = this.removeFromGroups(entity);
      const newGroups = this.addToGroup(entity, groupId);

      if (_.size(oldGroups) > 0) {
        entity.recentGroups = _.difference(oldGroups, newGroups); // eslint-disable-line
      }
    }

    return groupsChanged;
  }

  spawnEntities() {
    let entities = 0;

    _.each(this.map.staticEntities, (key, tileIndex) => {
      const isMob = !!this.mobsDictionary.getData(key);
      const isNpc = !!this.npcsDictionary.getData(key);
      const isItem = !!this.itemsDictionary.getData(key);

      const itemData = isItem
        ? this.itemsDictionary.getData(key)
        : null;

      const npcData = isNpc
        ? this.npcsDictionary.getData(key)
        : itemData;

      const info = isMob
        ? this.mobsDictionary.getData(key)
        : npcData;

      const position = this.map.indexToGridPosition(tileIndex);
      position.x += 1;

      if (!info || info === 'null') {
        if (this.debug) {
          log.notice(
            `Unknown object spawned at: ${position.x} ${position.y}`,
          );
        }

        return;
      }

      const typeNpc = isNpc ? 3 : 4;
      const instanceType = isMob ? 2 : typeNpc;

      const instance = Utils.generateInstance(
        instanceType,
        info.id + entities,
        position.x + entities,
        position.y,
      );

      if (isMob) {
        const mob = new Mob(info.id, instance, position.x, position.y);

        mob.static = true;

        mob.onRespawn(() => {
          mob.dead = false;
          mob.refresh();
          this.addMob(mob);
        });

        this.addMob(mob);
      }

      if (isNpc) {
        const npc = new NPC(info.id, instance, position.x, position.y);
        this.addNPC(npc);
      } else if (!isMob && isItem) {
        const item = this.createItem(info.id, instance, position.x, position.y);
        item.static = true;
        this.addItem(item);
      }

      entities += 1;
    });

    log.notice(`Spawned ${entities} entities!`);
  }

  spawnChests() {
    let chests = 0;

    _.each(this.map.chests, (info) => {
      this.spawnChest(info.i, info.x, info.y, true);

      chests += 1;
    });

    log.notice(`Spawned ${chests} static chests`);
  }

  spawnMob(id, x, y) {
    const instance = Utils.generateInstance(2, id, x + id, y);
    const mob = new Mob(id, instance, x, y);

    if (!MobsDictionary.exists(id)) {
      log.notice('Cannot spawn mob', id);
      return null;
    }

    this.addMob(mob);
    return mob;
  }

  spawnChest(items, x, y, staticChest) {
    const chestCount = Object.keys(this.chests).length;
    const instance = Utils.generateInstance(5, 194, chestCount, x, y);
    const chest = new Chest(194, instance, x, y);

    chest.ItemsDictionary = items;

    if (staticChest) {
      chest.static = staticChest;
      chest.onRespawn(this.addChest.bind(this, chest));
    }

    /**
     * Pretty simple concept, detect when the player opens the chest
     * then remove it and drop an item instead. Give it a 25 second
     * cooldown prior to respawning and voila.
     */
    chest.onOpen(() => {
      this.removeChest(chest);
      this.dropItem(ItemsDictionary.stringToId(chest.getItem()), 1, chest.x, chest.y);
    });

    this.addChest(chest);
    return chest;
  }

  createItem(id, instance, x, y) {
    let item;

    if (ItemsDictionary.hasPlugin(id)) {
      item = new (ItemsDictionary.isNewPlugin(id))(id, instance, x, y);
    } else {
      item = new Item(id, instance, x, y);
    }

    return item;
  }

  dropItem(id, count, x, y) {
    const
      instance = Utils.generateInstance(
        4,
        id + Object.keys(this.entities).length,
        x,
        y,
      );

    const item = this.createItem(id, instance, x, y);

    item.count = count;
    item.dropped = true;

    this.addItem(item);
    item.despawn();

    item.onBlink(() => {
      this.pushBroadcast(new Messages.Blink(item.instance));
    });

    item.onDespawn(() => {
      this.removeItem(item);
    });
  }

  pushEntities(player) {
    let entities;

    if (!player || !(player.group in this.groups)) {
      return;
    }

    entities = _.keys(this.groups[player.group].entities);
    entities = _.reject(entities, instance => instance === player.instance);
    entities = _.map(entities, instance => parseInt(instance, 10));

    if (entities) {
      player.send(new Messages.List(entities));
    }
  }

  addEntity(entity) {
    if (entity.instance in this.entities) {
      log.notice(`Entity ${entity.instance} ${entity.type} already exists.`,
        this.entities[entity.instance].id,
        this.entities[entity.instance].type);
      return;
    }

    this.entities[entity.instance] = entity;

    if (entity.type !== 'projectile') {
      this.handleEntityGroup(entity);
    }

    if (entity.x > 0 && entity.y > 0) {
      this.getGrids().addToEntityGrid(entity, entity.x, entity.y);
    }

    entity.onSetPosition(() => {
      this.getGrids().updateEntityPosition(entity);

      if (entity.isMob() && entity.isOutsideSpawn()) {
        entity.removeTarget();
        entity.combat.forget();
        entity.combat.stop();

        entity.return();

        this.pushBroadcast(
          new Messages.Combat(
            Packets.CombatOpcode.Finish,
            null,
            entity.instance,
          ),
        );
        this.pushBroadcast(
          new Messages.Movement(Packets.MovementOpcode.Move, [
            entity.instance,
            entity.x,
            entity.y,
            false,
            false,
          ]),
        );
      }
    });

    if (entity instanceof Character) {
      entity.getCombat().setWorld(this);

      entity.onStunned((stun) => {
        this.pushToAdjacentGroups(
          entity.group,
          new Messages.Movement(Packets.MovementOpcode.Stunned, [
            entity.instance,
            stun,
          ]),
        );
      });
    }
  }

  addPlayer(player) {
    this.addEntity(player);
    this.players[player.instance] = player;

    if (this.populationCallback) this.populationCallback();
  }

  addToPackets(player) {
    this.packets[player.instance] = [];
  }

  addNPC(npc) {
    this.addEntity(npc);
    this.npcsDictionary[npc.instance] = npc;
  }

  addMob(mob) {
    if (!MobsDictionary.exists(mob.id)) {
      log.error(`Cannot spawn mob. ${mob.id} does not exist.`);
      return;
    }

    this.addEntity(mob);
    this.mobsDictionary[mob.instance] = mob;

    mob.addToChestArea(this.getChestAreas());

    mob.onHit((attacker) => {
      if (mob.isDead() || mob.combat.started) return;

      mob.combat.begin(attacker);
    });
  }

  addItem(item) {
    if (item.static) {
      item.onRespawn(this.addItem.bind(this, item));
    }

    this.addEntity(item);
    this.itemsDictionary[item.instance] = item;
  }

  addProjectile(projectile) {
    this.addEntity(projectile);
    this.projectiles[projectile.instance] = projectile;
  }

  addChest(chest) {
    this.addEntity(chest);
    this.chests[chest.instance] = chest;
  }

  removeEntity(entity) {
    if (entity.instance in this.entities) {
      delete this.entities[entity.instance];
    }

    if (entity.instance in this.mobsDictionary) {
      delete this.mobsDictionary[entity.instance];
    }

    if (entity.instance in this.itemsDictionary) {
      delete this.itemsDictionary[entity.instance];
    }

    this.getGrids().removeFromEntityGrid(entity, entity.x, entity.y);
    this.removeFromGroups(entity);
  }

  cleanCombat(entity) {
    entity.combat.stop();

    _.each(this.entities, (oEntity) => {
      if (oEntity instanceof Character && oEntity.combat.hasAttacker(entity)) {
        oEntity.combat.removeAttacker(entity);
      }
    });
  }

  removeItem(item) {
    this.removeEntity(item);
    this.pushBroadcast(new Messages.Despawn(item.instance));

    if (item.static) item.respawn();
  }

  removePlayer(player) {
    this.pushToAdjacentGroups(
      player.group,
      new Messages.Despawn(player.instance),
    );

    if (player.ready) player.save();

    if (this.populationCallback) this.populationCallback();

    this.removeEntity(player);

    this.cleanCombat(player);

    if (player.isGuest) this.database.delete(player);

    delete this.players[player.instance];
    delete this.packets[player.instance];
  }

  removeProjectile(projectile) {
    this.removeEntity(projectile);

    delete this.projectiles[projectile.instance];
  }

  removeChest(chest) {
    this.removeEntity(chest);
    this.pushBroadcast(new Messages.Despawn(chest.instance));

    if (chest.static) chest.respawn();
    else delete this.chests[chest.instance];
  }

  playerInWorld(username) {
    for (const id in this.players) {
      if (this.players.hasOwnProperty(id) && this.players[id].username === username) {
        return true;
      }
    }

    return false;
  }

  getPlayerByName(username) {
    for (const id in this.players) {
      if (this.players.hasOwnProperty(id)) {
        if (this.players[id].username.toLowerCase() === username.toLowerCase()) {
          return this.players[id];
        }
      }
    }

    return null;
  }

  saveAll() {
    _.each(this.players, (player) => {
      player.save();
    });
  }

  getPVPAreas() {
    return this.map.areas.PVP.pvpAreas;
  }

  getMusicAreas() {
    return this.map.areas.Music.musicAreas;
  }

  getChestAreas() {
    return this.map.areas.Chests.chestAreas;
  }

  getGrids() {
    return this.map.grids;
  }

  getPopulation() {
    return _.size(this.players);
  }

  onPlayerConnection(callback) {
    this.playerConnectCallback = callback;
  }

  onPopulationChange(callback) {
    this.populationCallback = callback;
  }
}

import _ from 'underscore';
import ShutdownHook from 'shutdown-hook';
import log from './util/log';
import config from '../config.json';
import MySQL from './database/mysql';
import WebSocket from './network/websocket';
import Parser from './util/parser';
import Bot from '../../tools/bot/bot';
import Utils from './util/utils';
import World from './game/world';

let allowConnections = false;
const worlds = [];
let database;
let worldsCreated = 0;

function loadParser() {
  const parsed = new Parser();
  log.notice('Parser loaded');
  return parsed;
}

function saveAll() {
  _.each(worlds, (world) => {
    world.saveAll();
  });

  const plural = worlds.length > 1;

  log.notice(
    `Saved players for ${worlds.length} world${plural ? 's' : ''}.`,
  );
}

function allWorldsCreated() {
  log.notice(
    `Finished creating ${
      worlds.length
    } world${
      worlds.length > 1 ? 's' : ''
    }!`,
  );
  allowConnections = true;

  const host = config.host === '0.0.0.0' ? 'localhost' : config.host;
  log.notice(`Connect locally via http://${host}:${config.port}`);
}

function onWorldLoad() {
  worldsCreated += 1;
  if (worldsCreated === worlds.length) {
    allWorldsCreated();
  }
}

function initializeWorlds() {
  Object.keys(worlds).forEach((worldId) => {
    worlds[worldId].load(onWorldLoad);
  });
}

function getPopulations() {
  const counts = [];

  Object.keys(worlds).forEach((index) => {
    counts.push(worlds[index].getPopulation());
  });

  return counts;
}

function Main() {
  log.notice(`Initializing ${config.name} game engine...`);

  const shutdownHook = new ShutdownHook();
  const stdin = process.openStdin();
  const webSocket = new WebSocket.Server(config.host, config.port, config.gver);

  if (!config.offlineMode) {
    database = new MySQL(
      config.mysqlHost,
      config.mysqlPort,
      config.mysqlUser,
      config.mysqlPassword,
      config.mysqlDatabase,
    );
  }

  webSocket.onConnect((connection) => {
    if (!allowConnections) {
      connection.sendUTF8('disallowed');
      connection.close();
    }

    let world;

    for (let i = 0; i < worlds.length; i += 1) {
      if (worlds[i].playerCount < worlds[i].maxPlayers) {
        world = worlds[i];
        break;
      }
    }

    if (world) {
      world.playerConnectCallback(connection);
    } else {
      log.notice('Worlds are currently full, closing...');

      connection.sendUTF8('full');
      connection.close();
    }
  });

  setTimeout(() => {
    log.notice('setting timeout, initializing worlds');
    for (let i = 0; i < config.worlds; i += 1) {
      worlds.push(new World(i + 1, webSocket, database));
    }

    loadParser();
    initializeWorlds();
  }, 200);

  webSocket.onRequestStatus(() => JSON.stringify(getPopulations()));

  webSocket.onError(() => {
    log.notice('Web Socket has encountered an error.');
  });

  /**
   * We want to generate worlds after the socket
   * has finished initializing.
   */
  process.on('SIGINT', () => {
    shutdownHook.register();
  });

  process.on('SIGQUIT', () => {
    shutdownHook.register();
  });

  shutdownHook.on('ShutdownStarted', () => {
    saveAll();
  });

  stdin.addListener('data', (data) => {
    /**
     * We have to cleanse the raw message because of the \n
     */
    const message = data.toString().replace(/(\r\n|\n|\r)/gm, '');
    const type = message.charAt(0);

    if (type !== '/') {
      return;
    }

    const blocks = message.substring(1).split(' ');
    const command = blocks.shift();

    if (!command) {
      return;
    }

    switch (command) {
      case 'stop':
        log.notice('Safely shutting down the server...');
        saveAll();
        process.exit();
        break;

      case 'saveall':
        saveAll();
        break;

      case 'alter':
        if (blocks.length !== 3) {
          log.error('Invalid command format. /alter [database] [table] [type]');
          return;
        }

        if (!database) {
          log.error(
            `The database server is not available for this instance of ${
              config.name
            }.`,
          );
          log.error(
            'Ensure that the database is enabled in the server configuration.',
          );
          return;
        }

        const db = blocks.shift(); // eslint-disable-line
        const table = blocks.shift(); // eslint-disable-line
        const dType = blocks.shift(); // eslint-disable-line

        database.alter(db, table, dType);
        break;

      case 'bot':
        let count = parseInt(blocks.shift(), 10); // eslint-disable-line

        if (!count) {
          count = 1;
        }

        Bot(worlds[0], count);
        break;

      case 'test':
        Utils.test();
        break;
      default:
        break;
    }
  });
}

Main();

/**
 * This is an abstract file for Area,
 * it encompasses the dimensions and all
 * entities in it.
 */
export default class Area {
  constructor(id, x, y, width, height) {
    this.id = id;
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;

    this.entities = [];
    this.items = [];

    this.hasRespawned = true;
    this.chest = null;

    this.maxEntities = 0;
  }

  contains(x, y) {
    return (
      x >= this.x
      && y >= this.y
      && x < this.x + this.width
      && y < this.y + this.height
    );
  }

  addEntity(entity) {
    if (this.entities.indexOf(entity) > 0) {
      return;
    }

    this.entities.push(entity);
    entity.area = this; // eslint-disable-line

    if (this.spawnCallback) {
      this.spawnCallback();
    }
  }

  removeEntity(entity) {
    const index = this.entities.indexOf(entity);

    if (index > -1) this.entities.splice(index, 1);

    if (this.entities.length === 0 && this.emptyCallback) this.emptyCallback();
  }

  isFull() {
    return this.entities.length >= this.maxEntities;
  }

  setMaxEntities(maxEntities) {
    this.maxEntities = maxEntities;
  }

  onEmpty(callback) {
    this.emptyCallback = callback;
  }

  onSpawn(callback) {
    this.spawnCallback = callback;
  }
}

import _ from 'underscore';
import log from '../../util/log';
import Area from '../area';
import map from '../../../data/map/world_server.json';

export default class ChestAreas {
  constructor(world) {
    this.world = world;

    this.chestAreas = [];

    this.load();
  }

  load() {
    _.each(map.chestAreas, (m) => {
      const chestArea = new Area(m.id, m.x, m.y, m.width, m.height);

      chestArea.maxEntities = m.entities;
      chestArea.items = m.i;
      chestArea.cX = m.tx;
      chestArea.cY = m.ty;

      this.chestAreas.push(chestArea);

      chestArea.onEmpty(() => {
        this.spawnChest(this);
      });

      chestArea.onSpawn(() => {
        this.removeChest(this);
      });
    });

    log.notice(`Loaded ${this.chestAreas.length} chest areas.`);
  }

  standardize() {
    _.each(this.chestAreas, (chestArea) => {
      chestArea.setMaxEntities(chestArea.entities.length);
    });
  }

  spawnChest(chestArea) {
    chestArea.chest = this.world.spawnChest( // eslint-disable-line
      chestArea.items,
      chestArea.cX,
      chestArea.cY,
      false,
    );
  }

  removeChest(chestArea) {
    if (!chestArea.chest) {
      return;
    }

    this.world.removeChest(chestArea.chest);

    chestArea.chest = null; // eslint-disable-line
  }
}

