import _ from 'underscore';

/**
 * This class is another version of the grid generation
 * system in the client side. It's used to simplify location
 * of all the entities in the world.
 */
export default class Grids {
  constructor(map) {
    this.map = map;

    this.entityGrid = [];

    this.load();
  }

  load() {
    for (let i = 0; i < this.map.height; i += 1) {
      this.entityGrid[i] = [];

      for (let j = 0; j < this.map.width; j += 1) this.entityGrid[i][j] = {};
    }
  }

  updateEntityPosition(entity) {
    if (entity && entity.oldX === entity.x && entity.oldY === entity.y) return;

    this.removeFromEntityGrid(entity, entity.oldX, entity.oldY);
    this.addToEntityGrid(entity, entity.x, entity.y);

    entity.updatePosition();
  }

  addToEntityGrid(entity, x, y) {
    if (
      entity
      && x > 0
      && y > 0
      && x < this.map.width
      && x < this.map.height
      && this.entityGrid[y][x]
    ) this.entityGrid[y][x][entity.instance] = entity;
  }

  removeFromEntityGrid(entity, x, y) {
    if (
      entity
      && x > 0
      && y > 0
      && x < this.map.width
      && y < this.map.height
      && this.entityGrid[y][x]
      && entity.instance in this.entityGrid[y][x]
    ) delete this.entityGrid[y][x][entity.instance];
  }

  getSurroundingEntities(entity, radius, include) {
    const entities = [];

    if (!this.checkBounds(entity.x, entity.y, radius)) {
      return [];
    }

    for (let i = -radius; i < radius + 1; i += 1) {
      for (let j = -radius; j < radius + 1; j += 1) {
        const pos = this.entityGrid[entity.y + i][entity.x + j];

        if (_.size(pos) > 0) {
          _.each(pos, (pEntity) => {
            if (!include && pEntity.instance !== entity.instance) {
              entities.push(pEntity);
            }
          });
        }
      }
    }

    return entities;
  }

  checkBounds(x, y, radius) {
    return (
      x + radius < this.map.width
      && x - radius > 0
      && y + radius < this.map.height
      && y - radius > 0
    );
  }
}

import _ from 'underscore';
import map from '../../data/map/world_server.json';

export default class Groups {
  constructor(mapData) {
    this.map = mapData || map;

    this.width = this.map.width;
    this.height = this.map.height;

    this.zoneWidth = this.map.zoneWidth;
    this.zoneHeight = this.map.zoneHeight;

    this.groupWidth = this.map.groupWidth;
    this.groupHeight = this.map.groupHeight;

    this.linkedGroups = {};

    this.loadDoors();
  }

  loadDoors() {
    const {
      doors,
    } = map;

    _.each(doors, (door) => {
      const groupId = this.groupIdFromPosition(door.x, door.y);
      const linkedGroupId = this.groupIdFromPosition(door.tx, door.ty);
      const linkedGroupPosition = this.groupIdToPosition(linkedGroupId);

      if (groupId in this.linkedGroups) {
        this.linkedGroups[groupId].push(linkedGroupPosition);
      } else {
        this.linkedGroups[groupId] = [linkedGroupPosition];
      }
    });
  }

  getAdjacentGroups(id) {
    const position = this.groupIdToPosition(id);
    const {
      x,
      y,
    } = position;
    const list = [];

    for (let i = -1; i <= 1; i += 1) {
      for (let j = -1; j <= 1; j += 1) {
        list.push({
          x: x + j,
          y: y + i,
        });
      }
    }

    _.each(this.linkedGroups[id], (groupPosition) => {
      if (!_.any(list, pos => pos.x === x && pos.y === y)) {
        list.push(groupPosition);
      }
    });

    return _.reject(list, (groupPosition) => {
      const gX = groupPosition.x;
      const gY = groupPosition.y;

      return (
        gX < 0 || gY < 0 || gX >= this.groupWidth || gY >= this.groupHeight
      );
    });
  }

  forEachGroup(callback) {
    for (let x = 0; x < this.groupWidth; x += 1) for (let y = 0; y < this.groupHeight; y += 1) callback(`${x}-${y}`);
  }

  forEachAdjacentGroup(groupId, callback) {
    if (!groupId) return;

    _.each(this.getAdjacentGroups(groupId), (position) => {
      callback(`${position.x}-${position.y}`);
    });
  }

  groupIdFromPosition(x, y) {
    return (
      `${Math.floor(x / this.zoneWidth)}-${Math.floor(y / this.zoneHeight)}`
    );
  }

  groupIdToPosition(id) {
    const position = id.split('-');

    return {
      x: parseInt(position[0], 10),
      y: parseInt(position[1], 10),
    };
  }
}

import fs from 'fs';
import _ from 'underscore';
import log from '../util/log';
import Modules from '../util/modules';
import Utils from '../util/utils';
import Groups from './groups';
import PVPAreas from './areas/pvpareas';
import MusicAreas from './areas/musicareas';
import ChestAreas from './areas/chestareas';
import Grids from './grids';

import gridData from '../../data/map/collisions.json';
import map from '../../data/map/world_server.json';
import config from '../../config.json';

export default class Map {
  constructor(world) {
    this.world = world;
    this.ready = false;
    this.load();
    this.groups = new Groups(this);
    this.grids = new Grids(this);
  }

  load() {
    this.width = map.width;
    this.height = map.height;
    this.collisions = map.collisions;
    this.bossAreas = map.bossAreas;
    this.roamingAreas = map.roamingAreas;
    this.chestAreas = map.chestAreas;
    this.chests = map.chests;
    this.staticEntities = map.staticEntities;

    this.zoneWidth = 30;
    this.zoneHeight = 15;

    this.groupWidth = Math.floor(this.width / this.zoneWidth);
    this.groupHeight = Math.floor(this.height / this.zoneHeight);

    this.areas = {};

    this.loadAreas();
    this.loadCollisions();
    this.loadDoors();

    this.ready = true;
  }

  /**
   * The structure for the new this.areas is as follows:
   *
   * this.areas = {
   *      pvpAreas = {
   *          allPvpAreas
   *      }
   *
   *      musicAreas = {
   *          allMusicAreas
   *      }
   *
   *      ...
   * }
   */
  loadAreas() {
    this.areas.PVP = new PVPAreas();
    this.areas.Music = new MusicAreas();
    this.areas.Chests = new ChestAreas(this.world);
  }

  loadDoors() {
    this.doors = {};

    _.each(map.doors, (door) => {
      let orientation;

      switch (door.to) {
        default:
          break;
        case 'u':
          orientation = Modules.Orientation.Up;
          break;

        case 'd':
          orientation = Modules.Orientation.Down;
          break;

        case 'l':
          orientation = Modules.Orientation.Left;
          break;

        case 'r':
          orientation = Modules.Orientation.Right;
          break;
      }

      this.doors[this.gridPositionToIndex(door.x, door.y)] = {
        x: door.tx,
        y: door.ty,
        orientation,
        portal: door.p === 1,
        level: door.l,
        achievement: door.a,
        rank: door.r,
      };
    });
  }

  isDoor(x, y) {
    return !!this.doors[this.gridPositionToIndex(x, y)];
  }

  getDoorDestination(x, y) {
    return this.doors[this.gridPositionToIndex(x, y)];
  }

  loadCollisions() {
    const location = `${__dirname}/../../data/map/collisions.json`;

    this.grid = null;

    fs.exists(location, (exists) => {
      if (!exists || config.forceCollisions) {
        log.notice('Generating the collision grid...');

        this.grid = [];

        let tileIndex = 0;

        for (let i = 0; i < this.height; i += 1) {
          this.grid[i] = [];
          for (let j = 0; j < this.width; j += 1) {
            if (_.include(this.collisions, tileIndex)) {
              this.grid[i][j] = 1;
            } else {
              this.grid[i][j] = 0;
            }

            tileIndex += 1;
          }
        }

        fs.writeFile(location, JSON.stringify(this.grid), (err) => {
          if (err) {
            log.notice(`An error has occurred: ${err}`);
            return;
          }

          log.notice('The collision grid has been successfully generated!');

          this.done();
        });
      } else {
        this.grid = gridData;
        this.done();
      }
    });
  }

  isValidPosition(x, y) {
    return (
      Utils.isInt(x)
      && Utils.isInt(y)
      && !this.isOutOfBounds(x, y)
      && !this.isColliding(x, y)
    );
  }

  isOutOfBounds(x, y) {
    return x < 0 || x >= this.width || y < 0 || y >= this.height;
  }

  isColliding(x, y) {
    if (this.isOutOfBounds(x, y)) {
      return false;
    }

    return this.grid[y][x] === 1;
  }

  indexToGridPosition(tileIndex) {
    tileIndex -= 1; // eslint-disable-line

    const x = this.getX(tileIndex + 1, this.width);
    const y = Math.floor(tileIndex / this.width);

    return {
      x,
      y,
    };
  }

  gridPositionToIndex(x, y) {
    return y * this.width + x + 1;
  }

  getX(index, width) {
    if (index === 0) {
      return 0;
    }

    return index % width === 0 ? width - 1 : (index % width) - 1;
  }

  getRandomPosition(area) {
    const pos = {};
    let valid = false;

    while (!valid) {
      pos.x = area.x + Utils.randomInt(0, area.width + 1);
      pos.y = area.y + Utils.randomInt(0, area.height + 1);
      valid = this.isValidPosition(pos.x, pos.y);
    }

    return pos;
  }

  inArea(posX, posY, x, y, width, height) {
    return posX >= x && posY >= y && posX <= width + x && posY <= height + y;
  }

  inTutorialArea(entity) {
    if (entity.x === -1 || entity.y === -1) return true;

    return (
      this.inArea(entity.x, entity.y, 13, 553, 7, 7)
      || this.inArea(entity.x, entity.y, 15, 13, 11, 12)
    );
  }

  done() {
    if (this.readyCallback) this.readyCallback();
  }

  isReady(callback) {
    this.readyCallback = callback;
  }
}

