  loadShops() {
    let shopCounter = 0;

    _.each(ShopsData, (value, key) => {
      key = key.toLowerCase();

      this.shopsDictionary.setData(key, {
        key,
        id: value.npcId,
        items: value.items,
        count: value.count,
        prices: value.prices,
      });
      shopCounter += 1;
    });

    log.notice(`Finished loading ${shopCounter} shops.`);
  }

  loadLevels() {
    Formulas.LevelExp[0] = 0;

    for (let i = 1; i < 130; i += 1) {
      const points = Math.floor(0.25 * Math.floor(i + 300 * Math.pow(2, i / 7)));
      Formulas.LevelExp[i] = points + Formulas.LevelExp[i - 1];
    }
  }
}

import fs from 'fs';

const Filter = /^([^\.].*)\.js$/;

function identity(val) {
  return val;
}

export default function requireItems(directory) {
  const files = fs.readdirSync(directory);
  const modules = {};
  const resolve = identity;

  files.forEach((file) => {
    const match = file.match(Filter);

    if (match) {
      modules[match[1]] = resolve(require(directory + file));
    }
  });

  return modules;
}

